import React, { useState } from 'react';
import '../styles/PetCard.css';

const PetCard = ({ pet }) => {
  const [favorited, setFavorited] = useState(false);

  const handleToggleFavorite = () => {
    setFavorited(!favorited);
  };

  return (
    <div className="pet-card">
      <img src={pet.photos[0]} alt={pet.name} className="pet-card-image" />
      <h3>
        {pet.name} | {pet.distance} miles away{' '}
      </h3>
      <div className="pet-card-info">
        <div>
          <p>
            {pet.age} | {pet.gender} | {pet.size} | {pet.breed}
          </p>
        </div>
      </div>
      <p className="pet-card-description">
        {/* truncatedDescription here */}
      </p>
      <div className="pet-card-footer">
        <button
          className={`favorite-heart ${favorited ? 'favorited' : ''}`}
          onClick={handleToggleFavorite}
        >
          &#10084;
        </button>
        <button className="more-info-button">More Info</button>
      </div>
    </div>
  );
};

export default PetCard;
