import React, { useState, useEffect } from 'react';
import '../styles/SearchBar.css';

const SearchBar = ({ onSearch }) => {
  const [searchText, setSearchText] = useState('');
  const [petType, setPetType] = useState('');
  const [showLocationOptions, setShowLocationOptions] = useState(false);

  // Function to initialize Google Places Autocomplete
  const initializePlacesAutocomplete = () => {
    const input = document.getElementById('location-input');
    const options = {
      types: ['(cities)'],
    };
    const autocomplete = new window.google.maps.places.Autocomplete(input, options);

    // Listen for the 'place_changed' event
    autocomplete.addListener('place_changed', () => {
      const place = autocomplete.getPlace();
      if (place.geometry) {
        setSearchText(place.formatted_address);
      }
    });
  };

  // Load the Google Places Autocomplete script once the component is mounted
  useEffect(() => {
    const script = document.createElement('script');
    script.src = `https://maps.googleapis.com/maps/api/js?key=YOUR_GOOGLE_PLACES_API_KEY&libraries=places`;
    script.async = true;
    script.defer = true;
    script.addEventListener('load', initializePlacesAutocomplete);
    document.head.appendChild(script);

    return () => {
      // Clean up: remove the script when the component is unmounted
      document.head.removeChild(script);
    };
  }, []);

