import React from 'react';
import popularDogBreeds from './popularDogBreeds';

function Filter({ filters, onFilterChange }) {
  // Create state variables for filter values
  const [selectedType, setSelectedType] = useState(filters.type || 'any');
  const [selectedBreed, setSelectedBreed] = useState(filters.breed || 'any');
  const [selectedAge, setSelectedAge] = useState(filters.age || 'any');
  const [selectedGender, setSelectedGender] = useState(filters.gender || 'any');
  const [selectedSize, setSelectedSize] = useState(filters.size || 'any');
  const [selectedCoat, setSelectedCoat] = useState(filters.coat || 'any');

  // Handle filter selection and update state variables
  const handleFilterSelect = (filterName, selectedValue) => {
    switch (filterName) {
      case 'type':
        setSelectedType(selectedValue);
        setSelectedBreed('any'); // Reset breed when type changes
        setSelectedAge('any'); // Reset age when type changes
        setSelectedGender('any'); // Reset gender when type changes
        setSelectedSize('any'); // Reset size when type changes
        setSelectedCoat('any'); // Reset coat when type changes
        break;
      case 'breed':
        setSelectedBreed(selectedValue);
        break;
      case 'age':
        setSelectedAge(selectedValue);
        break;
      case 'gender':
        setSelectedGender(selectedValue);
        break;
      case 'size':
        setSelectedSize(selectedValue);
        break;
      case 'coat':
        setSelectedCoat(selectedValue);
        break;
      default:
        break;
    }

    // Update parent component with the selected filter
    onFilterChange(filterName, selectedValue);
  };


  const renderDogFilters = () => {
    return (
      <>
        <div className="filter-group">
          <label htmlFor="breed">Breed:</label>
          <select
            id="breed"
            value={filters.breed}
            onChange={(event) => handleFilterSelect('breed', event)}
          >
            <option value="any">Any</option>
            {popularDogBreeds.map((breed, index) => (
            <option key={index} value={breed.toLowerCase()}>
              {breed}
            </option>
          ))}
          </select>
        </div>
        <div className="filter-group">
          <label htmlFor="age">Age:</label>
          <select
            id="age"
            value={filters.age}
            onChange={(event) => handleFilterSelect('age', event)}
          >
            <option value="any">Any</option>
            <option value="young">Young</option>
            <option value="adult">Adult</option>
            <option value="senior">Senior</option>
          </select>
        </div>
        <div className="filter-group">
          <label htmlFor="gender">Gender:</label>
          <select
            id="gender"
            value={filters.gender}
            onChange={(event) => handleFilterSelect('gender', event)}
          >
            <option value="any">Any</option>
            <option value="male">Male</option>
            <option value="female">Female</option>
          </select>
        </div>
        <div className="filter-group">
          <label htmlFor="size">Size:</label>
          <select
            id="size"
            value={filters.size}
            onChange={(event) => handleFilterSelect('size', event)}
          >
            <option value="any">Any</option>
            <option value="medium">Medium</option>
            <option value="large">Large</option>
            {/* Add more size options based on  API data */}
          </select>
        </div>
        <div className="filter-group">
          <label htmlFor="coat">Coat:</label>
          <select
            id="coat"
            value={filters.coat}
            onChange={(event) => handleFilterSelect('coat', event)}
          >
            <option value="any">Any</option>
            <option value="short">Short</option>
            <option value="long">Long</option>
            {/* Add more coat options based on  API data */}
          </select>
        </div>
        {/* Add more dog-specific filters here */}
      </>
    );
  };

  const renderCatFilters = () => {
    return (
      <>
      <div className="filter-group">
        <label htmlFor="age">Age:</label>
        <select
          id="age"
          value={filters.age}
          onChange={(event) => handleFilterSelect('age', event)}
        >
          <option value="any">Any</option>
          <option value="kitten">Kitten</option>
          <option value="adult">Adult</option>
          <option value="senior">Senior</option>
        </select>
      </div>
      <div className="filter-group">
        <label htmlFor="size">Size:</label>
        <select
          id="size"
          value={filters.size}
          onChange={(event) => handleFilterSelect('size', event)}
        >
          <option value="any">Any</option>
          <option value="small">Small</option>
          <option value="medium">Medium</option>
          <option value="large">Large</option>
        </select>
      </div>
      <div className="filter-group">
        <label htmlFor="gender">Gender:</label>
        <select
          id="gender"
          value={filters.gender}
          onChange={(event) => handleFilterSelect('gender', event)}
        >
          <option value="any">Any</option>
          <option value="female">Female</option>
          <option value="male">Male</option>
        </select>
      </div>
      {/* Add more cat-specific filters here */}
    </>
  );
};

return (
  <div className="filters">
    <h2>Filters</h2>
    <div className="filter-group">
      <label htmlFor="type">Type:</label>
      <select
        id="type"
        value={selectedType}
        onChange={(event) => handleFilterSelect('type', event.target.value)}
      >
        <option value="any">Any</option>
        <option value="dog">Dog</option>
        <option value="cat">Cat</option>
      </select>
    </div>
    {/* Render dog filters if type is "dog" */}
    {selectedType.toLowerCase() === 'dog' && renderDogFilters()}
    {/* Render cat filters if type is "cat" */}
    {selectedType.toLowerCase() === 'cat' && renderCatFilters()}
  </div>
);
}


export default Filter;
