import React, { useState, useEffect } from 'react';
import '../styles/MainPage.css';
import PetCard from './PetCard';
import Filter from './Filter';

function MainPage({ favoritePets, addToFavorites, removeFromFavorites }) {
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(true);


  const [filters, setFilters] = useState({ type: 'any', breed: 'any', age: 'any', gender: 'any', size: 'any', coat: 'any' });

  useEffect(() => {
    // When the component mounts, fetch all animals
    fetchAllPets();
  }, []);

  const fetchAllPets = () => {
    const endpoint = 'http://localhost:3002/api/petfinder';

    fetch(endpoint)
    .then((response) => {
      console.log('API Response:', response);
      return response.json();
    })
    .then((data) => {
      console.log('API Data:', data);
      setSearchResults(data.animals || []);
      setLoading(false);
    })
    .catch((error) => {
      console.error('Error fetching data:', error.message);
      setLoading(false);
    });
  };

  const handleFilterChange = (filters) => {
    // Apply client-side filtering based on filters
    let filteredData = [...searchResults];

    // Filter by pet type
    if (filters.type !== 'any') {
      filteredData = filteredData.filter((pet) => {
        if (pet && pet.type) {
          return (pet.type).toLowerCase() === filters.type;
        }
        return false;
      });
    }

    // Filter by breed
    if (filters.breed !== 'any') {
      filteredData = filteredData.filter((pet) => {
        if (pet && pet.breed) {
          return (pet.breed).toLowerCase() === (filters.breed).toLowerCase();
        }
        return false;
      });
    }
    // Filter by age
    if (filters.age !== 'any') {
      filteredData = filteredData.filter((pet) => {
        if (pet && pet.age) {
          return pet.age === filters.age;
        }
        return false;
      });
    }
    // Filter by gender
    if (filters.gender !== 'any') {
      filteredData = filteredData.filter((pet) => {
        if (pet && pet.gender) {
          return (pet.gender).toLowerCase() === (filters.gender).toLowerCase();
        }
        return false;
      });
    }
    // Filter by size
    if (filters.size !== 'any') {
      filteredData = filteredData.filter((pet) => {
        if (pet && pet.size) {
          return (pet.size).toLowerCase() === (filters.size).toLowerCase();
        }
        return false;
      });
    }
    // Filter by coat
    if (filters.coat !== 'any') {
      filteredData = filteredData.filter((pet) => {
        if (pet && pet.coat) {
          return (pet.coat).toLowerCase() === (filters.coat).toLowerCase();
        }
        return false;
      });
    }

    setSearchResults(filteredData);
  };


  return (
    <div className="main-page">
      <div className="sidebar">
        <div className="filters">
          <Filter filters={filters} onFilterChange={handleFilterChange} />
        </div>
      </div>
      <div className="content">
        {loading ? (
          <p>Loading...</p>
        ) : (
          <div className="pet-card-list">
            {searchResults.map((pet) => (
              <PetCard
                key={pet.id}
                pet={pet}
                addToFavorites={addToFavorites}
                removeFromFavorites={removeFromFavorites}
                isFavorite={favoritePets.some((favoritePet) => favoritePet.id === pet.id)}
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default MainPage;
