import React, { useState, useEffect, useCallback, useRef } from 'react';
import '../styles/MainPage.css';
import PetCard from './PetCard';
import Filter from './Filter';

function MainPage({ favoritePets, addToFavorites, removeFromFavorites }) {
  const [searchResults, setSearchResults] = useState([]);
  const [filteredResults, setFilteredResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedFilters, setSelectedFilters] = useState({
    type: 'any',
    breed: 'any',
    age: 'any',
    gender: 'any',
    size: 'any',
    coat: 'any',
  });
  const itemsPerPage = 10;
  const minimumPets = 10;
  const fetchInProgress = useRef(false);
  const filterTimeout = useRef(null);

  const fetchAllPets = useCallback(() => {
    if (fetchInProgress.current) {
      return;
    }

    fetchInProgress.current = true;
    setLoading(true);

    const endpoint = `http://localhost:3002/api/petfinder?page=${currentPage}&perPage=${itemsPerPage}`;

    fetch(endpoint)
      .then((response) => response.json())
      .then((data) => {
        console.log('API Data:', data);

        const updatedResults = [...searchResults, ...(data.animals || [])];
        setSearchResults(updatedResults);

        if (updatedResults.length < minimumPets) {
          setCurrentPage(currentPage + 1);
        } else {
          setLoading(false);
        }
      })
      .catch((error) => {
        console.error('Error fetching data:', error.message);
        setLoading(false);
      })
      .finally(() => {
        fetchInProgress.current = false;
      });
  }, [currentPage, itemsPerPage, minimumPets, searchResults]);

  useEffect(() => {
    // Fetch all pets when the component mounts
    setSearchResults([]);
    setCurrentPage(1);
    fetchAllPets();
  }, [fetchAllPets]);

  const applyFilters = useCallback(() => {
    if (filterTimeout.current) {
      clearTimeout(filterTimeout.current);
    }

    filterTimeout.current = setTimeout(() => {
      const filteredResults = applyFilters(searchResults, selectedFilters);
      setFilteredResults(filteredResults);
    }, 300);
  }, [searchResults, selectedFilters]);

  const handleFilterSubmit = () => {
    applyFilters();
  };

  const handleSearchClick = () => {
    setSearchResults([]);
    setCurrentPage(1);
    fetchAllPets();
  };

  return (
    <div className="main-page">
      <div className="sidebar">
        <div className="filters">
          <Filter
            selectedFilters={selectedFilters} // Pass selectedFilters to the Filter component
            onFilterSubmit={handleFilterSubmit}
            setSelectedFilters={setSelectedFilters} // Pass setSelectedFilters to the Filter component
          />
        </div>
      </div>
      <div className="content">
        {!loading ? (
          <div className="pet-card-list">
            {filteredResults.length > 0
              ? filteredResults.map((pet) => (
                  <PetCard
                    key={pet.id}
                    pet={pet}
                    addToFavorites={addToFavorites}
                    removeFromFavorites={removeFromFavorites}
                    isFavorite={favoritePets.some((favoritePet) => favoritePet.id === pet.id)}
                  />
                ))
              : searchResults.map((pet) => (
                  <PetCard
                    key={pet.id}
                    pet={pet}
                    addToFavorites={addToFavorites}
                    removeFromFavorites={removeFromFavorites}
                    isFavorite={favoritePets.some((favoritePet) => favoritePet.id === pet.id)}
                  />
                ))}
          </div>
        ) : (
          <p>Loading...</p>
        )}
      </div>
    </div>
  );
}

export default MainPage;
