import React, { useState, useEffect } from 'react';
import PlacesAutocomplete, { geocodeByAddress, getLatLng } from 'react-places-autocomplete';
import '../styles/SearchBar.css';

const SearchBar = ({ onSearch }) => {
  const [searchText, setSearchText] = useState('');
  const [petType, setPetType] = useState('');
  const [address, setAddress] = useState('');
  const [suggestions, setSuggestions] = useState([]); // Store Google Maps suggestions

  useEffect(() => {
    // Load the Google Maps service and initialize it
    const initMap = () => {
      const sydney = new google.maps.LatLng(-33.867, 151.195);
      const map = new google.maps.Map(document.getElementById('map'), {
        center: sydney,
        zoom: 15
      });

      const infowindow = new google.maps.InfoWindow();
      const service = new google.maps.places.PlacesService(map);

      // Attach the service to the component's state
      setService(service);
    };

    // Initialize Google Maps service after the component mounts
    initMap();
  }, []);

  // Handle user input for the search bar
  const handleInput = (e) => {
    setSearchText(e);

    // Use the Google Maps service to suggest places as the user types
    if (service && e.length > 2) {
      const request = {
        query: e,
        fields: ['name', 'geometry'],
      };

      service.findPlaceFromQuery(request, (results, status) => {
        if (status === google.maps.places.PlacesServiceStatus.OK) {
          // Extract suggestions from results and set them to state
          setSuggestions(results);
        }
      });
    } else {
      setSuggestions([]); // Clear suggestions when input is less than 3 characters
    }
  };

  const handleSelect = async (selectedAddress) => {
    setAddress(selectedAddress);
    setSearchText(selectedAddress); // Update the input with the selected address
    setSuggestions([]); // Clear suggestions

    const results = await geocodeByAddress(selectedAddress);
    const latLng = await getLatLng(results[0]);

    // Now you have the latitude and longitude, which you can use for searching
    onSearch({ address: selectedAddress, latLng });
  };

  // Render your component as before, but include the suggestions
  return (
    <div className="search-bar">
      <PlacesAutocomplete
        value={searchText}
        onChange={handleInput}
        onSelect={handleSelect}
      >
        {({ getInputProps }) => (
          <div className="search-container">
            <div className="search-input-container">
              <input
                {...getInputProps()}
                type="text"
                placeholder="Enter zipcode, city, or state"
                className="search-input"
              />
              <div className="autocomplete-dropdown-container">
                {suggestions.map((suggestion) => {
                  const className = suggestion.active
                    ? 'suggestion-item--active'
                    : 'suggestion-item';
                  // Render the suggestions as dropdown items
                  return (
                    <div
                      key={suggestion.place_id} // Use a unique key
                      className={className}
                      onClick={() => handleSelect(suggestion.description)}
                    >
                      {suggestion.description}
                    </div>
                  );
                })}
              </div>
            </div>
            <select
              value={petType}
              onChange={(e) => setPetType(e.target.value)}
              className="search-select"
            >
              <option value="">Select pet type</option>
              <option value="dog">Dog</option>
              <option value="cat">Cat</option>
              <option value="horse">Horse</option>
              <option value="bird">Bird</option>
              <option value="furry">Furry</option>
              <option value="barnyard">Barnyard</option>
            </select>
            <button onClick={handleSearch} className="search-button">
              Search
            </button>
          </div>
        )}
      </PlacesAutocomplete>
    </div>
  );
};

export default SearchBar;
