import React, { useState, useEffect } from 'react';
import '../styles/AllPetsPage.css'; // You can create a CSS file for styling

function AllPetsPage({ match }) {
  const { petType } = match.params;
  const [pets, setPets] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAllPets = async () => {
      try {
        let endpoint = '';

        if (petType === 'dogs') {
          endpoint = 'https://api.petfinder.com/v2/types/dog';
        } else if (petType === 'cats') {
          endpoint = 'https://api.petfinder.com/v2/types/cat';
        } else if (petType === 'other-animals') {
          endpoint = 'https://api.petfinder.com/v2/animals';
        }

        const response = await fetch(endpoint);
        const data = await response.json();
        console.log(data);

        if (data && data.animals) {
          setPets(data.animals);
          setLoading(false);
        }
      } catch (error) {
        console.error(`Error fetching ${petType} data:`, error);
        setLoading(false);
      }
    };

    fetchAllPets(); // Fetch all pets when the component mounts
  }, [petType]);

  return (
    <div className="all-pets-page">
      <h2>{`All ${petType.charAt(0).toUpperCase() + petType.slice(1)}`}</h2>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="pet-list">
          {pets.map((pet) => (
            <div key={pet.id}>
              <h3>{pet.name}</h3>
              {/* Display other pet information */}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default AllPetsPage;
