import React from 'react';

function Filter({ filters, onFilterChange }) {
  const handleFilterSelect = (filterName, event) => {
    const selectedValue = event.target.value;
    // Ensure the value is lowercase, or set it to 'any' if it's undefined
    const lowercasedValue = selectedValue ? selectedValue : 'any';
    onFilterChange(filterName, lowercasedValue);
  };

  console.log('Current filters.type:', filters.type); // Debugging log


  const renderDogFilters = () => {
    return (
      <>
      <div className="filter-group">
          <label htmlFor="type">Type:</label>
          <select
            id="type"
            value="dog" // Set the value directly to "dog"
            disabled // Disable the select since it's always "dog"
          >
            <option value="dog">Dog</option>
          </select>
        </div>
        <div className="filter-group">
          <label htmlFor="breed">Breed:</label>
          <select
            id="breed"
            value={filters.breed}
            onChange={(event) => handleFilterSelect('breed', event)}
          >
            <option value="any">Any</option>
            <option value="akita">Akita</option>
            {/* Add more dog breed options based on your API data */}
          </select>
        </div>
        <div className="filter-group">
          <label htmlFor="age">Age:</label>
          <select
            id="age"
            value={filters.age}
            onChange={(event) => handleFilterSelect('age', event)}
          >
            <option value="any">Any</option>
            <option value="young">Young</option>
            <option value="adult">Adult</option>
            <option value="senior">Senior</option>
          </select>
        </div>
        <div className="filter-group">
          <label htmlFor="gender">Gender:</label>
          <select
            id="gender"
            value={filters.gender}
            onChange={(event) => handleFilterSelect('gender', event)}
          >
            <option value="any">Any</option>
            <option value="male">Male</option>
            <option value="female">Female</option>
          </select>
        </div>
        <div className="filter-group">
          <label htmlFor="size">Size:</label>
          <select
            id="size"
            value={filters.size}
            onChange={(event) => handleFilterSelect('size', event)}
          >
            <option value="any">Any</option>
            <option value="medium">Medium</option>
            <option value="large">Large</option>
            {/* Add more size options based on your API data */}
          </select>
        </div>
        <div className="filter-group">
          <label htmlFor="coat">Coat:</label>
          <select
            id="coat"
            value={filters.coat}
            onChange={(event) => handleFilterSelect('coat', event)}
          >
            <option value="any">Any</option>
            <option value="short">Short</option>
            <option value="long">Long</option>
            {/* Add more coat options based on your API data */}
          </select>
        </div>
        <div className="filter-group">
          <label htmlFor="tags">Tags:</label>
          <select
            id="tags"
            value={filters.tags}
            onChange={(event) => handleFilterSelect('tags', event)}
          >
            <option value="any">Any</option>
            <option value="cute">Cute</option>
            <option value="intelligent">Intelligent</option>
            {/* Add more tags options based on your API data */}
          </select>
        </div>
        {/* Add more dog-specific filters here */}
      </>
    );
  };

  const renderCatFilters = () => {
    return (
      <>
      <div className="filter-group">
        <label htmlFor="age">Age:</label>
        <select
          id="age"
          value={filters.age}
          onChange={(event) => handleFilterSelect('age', event)}
        >
          <option value="any">Any</option>
          <option value="kitten">Kitten</option>
          <option value="adult">Adult</option>
          <option value="senior">Senior</option>
        </select>
      </div>
      <div className="filter-group">
        <label htmlFor="size">Size:</label>
        <select
          id="size"
          value={filters.size}
          onChange={(event) => handleFilterSelect('size', event)}
        >
          <option value="any">Any</option>
          <option value="small">Small</option>
          <option value="medium">Medium</option>
          <option value="large">Large</option>
        </select>
      </div>
      <div className="filter-group">
        <label htmlFor="gender">Gender:</label>
        <select
          id="gender"
          value={filters.gender}
          onChange={(event) => handleFilterSelect('gender', event)}
        >
          <option value="any">Any</option>
          <option value="female">Female</option>
          <option value="male">Male</option>
        </select>
      </div>
      {/* Add more cat-specific filters here */}
    </>
  );
};

  return (
    <div className="filters">
      <h2>Filters</h2>
      {filters.type === 'dog' ? (
        <>
          {renderDogFilters()}
        </>
      ) : filters.type === 'cat' ? (
        <>
          {renderCatFilters()}
        </>
      ) : (
        <>
          <div className="filter-group">
            <label htmlFor="type">Type:</label>
            <select
              id="type"
              value={filters.type}
              onChange={(event) => handleFilterSelect('type', event)}
            >
              <option value="any">Any</option>
              <option value="dog">Dog</option>
              <option value="cat">Cat</option>
              <option value="others">Others</option>
            </select>
          </div>
        </>
      )}
    </div>
  );
}


export default Filter;
