import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import PetCard from './PetCard';
import '../styles/LocationSpecificPets.css';

function shuffleArray(array) {
  for (let i = array.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [array[i], array[j]] = [array[j], array[i]];
  }
}

const LocationSpecificPets = ({
  favoritePets,
  addToFavorites,
  removeFromFavorites
}) => {
  const location = useLocation();
  const state = location.state;

  const [currentPage, setCurrentPage] = useState(1);
  const [pets, setPets] = useState([]);
  const [loading, setLoading] = useState(true);
  const itemsPerPage = 20;

  useEffect(() => {
    if (!state || !state.data) {
      return;
    }

    async function fetchData() {
      setLoading(true);

      const animals = await fetchAnimalsByCategory(currentPage);

      shuffleArray(animals);

      setPets(animals);
      setLoading(false);
    }

    fetchData();
  }, [state, currentPage]);

  const fetchAnimalsByCategory = async (page) => {
    const { data, petType } = state;
    let endpoint;
    let animalData;

    if (petType === "dog" || petType === "cat") {
      endpoint = `http://localhost:3002/api/petfinder?type=${petType}&limit=40&page=${page}`;
    } else if (petType === "other") {
      const otherAnimalTypes = ["horse", "bird", "barnyard"];
      const animalPromises = otherAnimalTypes.map(async (animalType) => {
        const response = await fetch(
          `http://localhost:3002/api/petfinder?type=${animalType}&limit=${itemsPerPage}&page=${page}`
        );
        const data = await response.json();
        return data.animals || [];
      });

      const animalResponses = await Promise.all(animalPromises);
      animalData = animalResponses.flat();
    }

    try {
      if (animalData) {
        return animalData.filter((animal) => animal && animal.photos.length > 0);
      } else if (endpoint) {
        const response = await fetch(endpoint);
        const data = await response.json();
        if (data && data.animals) {
          return data.animals.filter((animal) => animal.photos.length > 0);
        }
      }
    } catch (error) {
      console.error('Error fetching data:', error.message);
    }

    return [];
  };

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handleNextPage = () => {
    if (currentPage < state.data.pagination.total_pages) {
      setCurrentPage(currentPage + 1);
    }
  };

  return (
    <div className="location-specific-pets">
      <h2 className="search-results-title">Search Results for:</h2>
      <h3 className="pet-type-title">All {state.petType}s in the {state.searchText} area</h3>
      <div className="pet-card-container">
        {loading ? (
          <p className="no-animals-message">Loading...</p>
        ) : (
          pets.length === 0 ? (
            <p className="no-animals-message">No animals found.</p>
          ) : (
            pets.map((pet) => (
              <PetCard
                key={pet.id}
                pet={pet}
                addToFavorites={addToFavorites}
                removeFromFavorites={removeFromFavorites}
                isFavorite={favoritePets.some((favoritePet) => favoritePet.id === pet.id)}
              />
            ))
          )
        )}
      </div>
      <div className="pagination">
        <button onClick={handlePreviousPage} disabled={currentPage === 1}>
          Previous Page
        </button>
        <span>Page {currentPage} of {state.data.pagination.total_pages}</span>
        <button onClick={handleNextPage} disabled={currentPage === state.data.pagination.total_pages}>
          Next Page
        </button>
      </div>
    </div>
  );
};

export default LocationSpecificPets;
