import React, { useState, useEffect } from 'react';
import '../styles/MainPage.css';
import SearchBar from './SearchBar';
import PetCard from './PetCard';
import Filter from './Filter';

function MainPage({ onFilterChange }) {
  const [filters, setFilters] = useState({
    size: 'any',
    breed: 'any',
    age: 'any',
    gender: 'any',
    shelter: 'any',
    petType: 'any', // Add petType filter
  });

  const [searchResults, setSearchResults] = useState([]); // Initialize searchResults state

  // Sample pet data (you can replace this with the data from your API)
  const sampleData = [
    {
      id: 1,
      name: 'Buddy',
      breed: 'Golden Retriever',
      age: 'Puppy',
      size: 'Medium',
      gender: 'Male',
      shelter: 'Happy Paws Shelter',
      photo: '/images/dog2.jpg',
      distance: '2 miles',
      type: "Dog"
    },
    {
      id: 2,
      name: 'puppyGirl',
      breed: 'Golden Retriever',
      age: 'Puppy',
      size: 'small',
      gender: 'Female',
      shelter: 'Happy Paws Shelter',
      photo: '/images/pup1.jpg',
      distance: '2 miles',
      type: "Dog"
    },
    {
      id: 3,
      name: 'Daisy',
      breed: 'Labrador',
      age: 'Adult',
      size: 'Large',
      gender: 'Female',
      shelter: 'Loving Hearts Rescue',
      photo: '/images/dog.jpg',
      distance: '5 miles',
      type: "Dog"
    },
    {
      id: 4,
      name: 'Kitty',
      breed: 'long hair',
      age: 'Kitten',
      size: 'small',
      gender: 'Female',
      shelter: 'Pawsome Haven',
      photo: '/images/kitten1.jpg',
      distance: '1 miles',
    },
    // Add more sample pet data as needed
  ];


  useEffect(() => {
    // Fetch and populate data from your API here and set it in setSearchResults
    // For now, using sampleData for demonstration
    setSearchResults(sampleData);
  }, []);

  const filterPets = () => {
    // Apply filters to searchResults based on the filters state
    let filteredData = [...sampleData]; // Initialize with full data

    if (filters.size !== 'any') {
      filteredData = filteredData.filter((pet) => pet.size === filters.size.toLowerCase());
    }
    if (filters.breed !== 'any') {
      filteredData = filteredData.filter((pet) => pet.breed === filters.breed);
    }
    if (filters.age !== 'any') {
      filteredData = filteredData.filter((pet) => pet.age === filters.age);
    }
    if (filters.gender !== 'any') {
      filteredData = filteredData.filter((pet) => pet.gender === filters.gender);
    }
    if (filters.shelter !== 'any') {
      // Assuming you have a shelter field in your pet data
      filteredData = filteredData.filter((pet) => pet.shelter === filters.shelter);
    }

    // Update searchResults with the filtered data
    setSearchResults(filteredData);
  };

  const handleFilterChange = (filterName, value) => {
    setFilters({
      ...filters,
      [filterName]: value,
    });

    // Call the filterPets function to apply the filters immediately
    filterPets();
  };

  return (
    <div className="main-page">
      <div className="search-bar">
        <SearchBar />
      </div>
      <div className="sidebar">
        <div className="filters">
          <Filter filters={filters} onFilterChange={handleFilterChange} />
        </div>
      </div>
      <div className="content">
        <div className="pet-card-list">
          {searchResults.map((pet) => (
            <PetCard key={pet.id} pet={pet} />
          ))}
        </div>
      </div>
    </div>
  );
}

export default MainPage;
