import React from 'react';
import '../styles/Resources.css';

const Resources = () => {
  // Define a list of image URLs
  const imageUrls = [
    'frontend/src/images/catanddog1.jpg',
    'frontend/src/images/catanddog1.jpg',
    'url_to_image3',
  ];

  return (
    <div className="resources-container">
      <h2>Resources</h2>
      {/* Map through the resources and render them with alternating images */}
      <div className="resource">
        <img
          src={imageUrls[0]}
          alt="Image 1"
          className="resource-image-left"
        />
        <h3>Adoption Information</h3>
        <p>Learn about the adoption process and requirements.</p>
      </div>
      <div className="resource">
        <h3>Pet Care Tips</h3>
        <p>Get advice on caring for your new furry friend.</p>
        <img
          src={imageUrls[1]}
          alt="Image 2"
          className="resource-image-right"
        />
      </div>
      <div className="resource">
        <img
          src={imageUrls[2]}
          alt="Image 3"
          className="resource-image-left"
        />
        <h3>Local Shelters</h3>
        <p>Find shelters and rescues near you.</p>
      </div>
    </div>
  );
};

export default Resources;
