import React, { useState, useEffect } from 'react';
import '../styles/MainPage.css';
import SearchBar from './SearchBar';
import PetCard from './PetCard';
import Filter from './Filter';
import SampleData from './SampleData';

function MainPage({ onFilterChange }) {
  const [filters, setFilters] = useState({
    type: 'any',
    species: 'any',
    age: 'any',
    gender: 'any',
    size: 'any',
    coat: 'any',
    tags: 'any',
    name: 'any',
    description: 'any',
    photos: 'any',
    status: 'any',
    contact: 'any'
  });

  const [searchResults, setSearchResults] = useState([]); // Initialize searchResults state

  useEffect(() => {
    // Fetch and populate data from your API here and set it in setSearchResults
    // For now, using sampleData for demonstration
    setSearchResults(SampleData);
  }, []);

  const filterPets = () => {
    // Initialize filteredData with the full dataset
    let filteredData = [...SampleData];

    // Filter by type
    if (filters.type !== 'any') {
      const filterType = filters.type.toLowerCase();
      filteredData = filteredData.filter((pet) => pet.type.toLowerCase() === filterType);
    }

    // Filter by species
    if (filters.species !== 'any') {
      const filterSpecies = filters.species.toLowerCase();
      filteredData = filteredData.filter((pet) => pet.species.toLowerCase() === filterSpecies);
    }

    // Filter by breed
    if (filters.breed !== 'any') {
      const filterBreed = filters.breed.toLowerCase();
      filteredData = filteredData.filter((pet) => pet.breeds.primary.toLowerCase() === filterBreed);
    }

    // Filter by age
    if (filters.age !== 'any') {
      const filterAge = filters.age.toLowerCase();
      filteredData = filteredData.filter((pet) => pet.age.toLowerCase() === filterAge);
    }

    // Filter by gender
    if (filters.gender !== 'any') {
      const filterGender = filters.gender.toLowerCase();
      filteredData = filteredData.filter((pet) => pet.gender.toLowerCase() === filterGender);
    }

    // Filter by size
    if (filters.size !== 'any') {
      const filterSize = filters.size.toLowerCase();
      filteredData = filteredData.filter((pet) => pet.size.toLowerCase() === filterSize);
    }

    // Filter by coat
    if (filters.coat !== 'any') {
      const filterCoat = filters.coat.toLowerCase();
      filteredData = filteredData.filter((pet) => pet.coat && pet.coat.toLowerCase() === filterCoat);
    }

    // Filter by tags
    if (filters.tags !== 'any') {
      const filterTags = filters.tags.toLowerCase();
      filteredData = filteredData.filter((pet) => pet.tags.some((tag) => tag.toLowerCase() === filterTags));
    }

    // Filter by name
    if (filters.name !== 'any') {
      const filterName = filters.name.toLowerCase();
      filteredData = filteredData.filter((pet) => pet.name.toLowerCase() === filterName);
    }

    // Filter by description
    if (filters.description !== 'any') {
      const filterDescription = filters.description.toLowerCase();
      filteredData = filteredData.filter((pet) => pet.description.toLowerCase() === filterDescription);
    }

    // Filter by photos
    if (filters.photos !== 'any') {
      const filterPhotos = filters.photos.toLowerCase();
      filteredData = filteredData.filter((pet) => pet.photos.some((photo) => photo.toLowerCase() === filterPhotos));
    }

    // Filter by status
    if (filters.status !== 'any') {
      const filterStatus = filters.status.toLowerCase();
      filteredData = filteredData.filter((pet) => pet.status.toLowerCase() === filterStatus);
    }

    // Filter by contact
    if (filters.contact !== 'any') {
      const filterContact = filters.contact.toLowerCase();
      filteredData = filteredData.filter((pet) => pet.contact.email.toLowerCase() === filterContact);
    }

    // Update searchResults with the filtered data
    setSearchResults(filteredData);
  };


  const handleFilterChange = (filterName, value) => {
    setFilters({
      ...filters,
      [filterName]: value,
    });

    filterPets();
  };

  return (
    <div className="main-page">
      <div className="search-bar">
        <SearchBar />
      </div>
      <div className="sidebar">
        <div className="filters">
          <Filter filters={filters} onFilterChange={handleFilterChange} />
        </div>
      </div>
      <div className="content">
        <div className="pet-card-list">
          {searchResults.map((pet) => (
            <PetCard key={pet.id} pet={pet} />
          ))}
        </div>
      </div>
    </div>
  );
}

export default MainPage;
