import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // Import useParams to access route parameters
import '../styles/AllPetsPage.css';

function AllPetsPage() {
  const { category } = useParams(); // Use the useParams hook to get the category from the route parameter
  const [pets, setPets] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Define the API endpoint based on the selected category
    let endpoint = '';

    if (category === 'dogs') {
      endpoint = 'https://api.petfinder.com/v2/types/dog';
    } else if (category === 'cats') {
      endpoint = 'https://api.petfinder.com/v2/types/cat'; // Replace with the actual endpoint for fetching cats
    } else if (category === 'other-animals') {
      endpoint = 'https://api.petfinder.com/v2/animals'; // Replace with the actual endpoint for fetching other animals
    } else {
      // Handle invalid categories or unknown values
      console.error('Invalid category:', category);
      setLoading(false);
      return;
    }

    const fetchData = async () => {
      try {
        const response = await fetch(endpoint);
        const data = await response.json();

        if (data && data.pets) {
          setPets(data.pets);
          setLoading(false);
        }
      } catch (error) {
        console.error(`Error fetching ${category} data:`, error);
        setLoading(false);
      }
    };

    fetchData();
  }, [category]); // Include category in the dependency array

  return (
    <div className="all-pets-page">
      <h2>{`All ${category.charAt(0).toUpperCase() + category.slice(1)}`}</h2>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="pet-list">
          {pets.map((pet) => (
            <div key={pet.id}>
              <h3>{pet.name}</h3>
              {/* Display other pet information */}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default AllPetsPage;
