import React, { useState } from 'react';
import '../styles/Favorites.css';

const Favorites = () => {
  const [favorites, setFavorites] = useState([]);

  // // Placeholder data for favorited pets
  // const favoritedPetsData = [
  //   {
  //     id: 1,
  //     name: 'Buddy',
  //     photo: '/images/dog.jpg',
  //     shelter: 'Happy Paws Shelter',
  //     favorited: true, // Initially favorited
  //   },
  //   {
  //     id: 2,
  //     name: 'Daisy',
  //     photo: '/images/dog2.jpg',
  //     shelter: 'Loving Hearts Rescue',
  //     favorited: false,
  //   },
  //   {
  //     id: 2,
  //     name: 'Daisy',
  //     photo: '/images/kitten1.jpg',
  //     shelter: 'Loving Hearts Rescue',
  //     favorited: false,
  //   },
  // ];


  const handleToggleFavorite = () => {
    // Check if the pet is already in favorites
    const isFavorite = favorites.some((favorite) => favorite.id === pet.id);

    if (isFavorite) {
      // If it's already a favorite, remove it
      const updatedFavorites = favorites.filter((favorite) => favorite.id !== pet.id);
      setFavorites(updatedFavorites);
    } else {
      // If it's not a favorite, add it
      setFavorites([...favorites, pet]);
    }
  };


  return (
    <div className="favorites-container">
      <h2>Favorites</h2>
      <div className="favorite-cards">
        {favorites.map((favorite) => (
          <PetCard key={favorite.id} pet={favorite} />
        ))}
      </div>
    </div>
  );
};

export default Favorites;
