import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/AllPetsPage.css';
import PetCard from './PetCard';

function shuffleArray(array) {
  for (let i = array.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [array[i], array[j]] = [array[j], array[i]];
  }
}

function AllPetsPage() {
  const { category } = useParams();
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [cache, setCache] = useState([]);
  const petsPerPage = 20;
  const otherAnimalTypes = ["horse", "bird", "barnyard"];
  const buttonsToShow = 9; // Number of buttons to show at once

  const fetchAnimalsByCategory = async (category, page) => {
    let endpoint;

    if (category === "dog" || category === "cat") {
      // API call for "dog" or "cat"
      endpoint = `http://localhost:3002/api/petfinder?type=${category}&limit=${petsPerPage}&page=${page}`;
    } else if (category === "other") {
      // API call for "other" category
      const animalType = otherAnimalTypes[page - 1] || otherAnimalTypes[0]; // Get the corresponding type
      endpoint = `http://localhost:3002/api/petfinder?type=${animalType}&limit=${petsPerPage}`;
    }

    try {
      const response = await fetch(endpoint);
      const data = await response.json();
      if (data && data.animals) {
        setCache(data.animals);
        setLoading(false);
      }
    } catch (error) {
      console.error('Error fetching data:', error.message);
    }
  };

  useEffect(() => {
    async function fetchData() {
      await fetchAnimalsByCategory(category, currentPage);
      // Scroll to the top when loading new animals
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }

    fetchData();
  }, [category, currentPage]);

  const handlePageChange = (newPage) => {
    if (newPage >= 1) {
      setCurrentPage(newPage);
    }
  };

  const pageButtons = category === 'other' ? otherAnimalTypes : Array.from({ length: 9 }, (_, i) => i + 1);

  return (
    <div className="all-pets-page">
      <h2>{`All ${category.charAt(0).toUpperCase() + category.slice(1)}s`}</h2>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="pet-list">
          {cache.map((pet) => (
            <PetCard key={pet.id} pet={pet} />
          ))}
        </div>
      )}
      <div className="pagination">
        <button
          onClick={() => handlePageChange(currentPage - 1)}
          disabled={currentPage === 1}
        >
          Previous
        </button>
        {pageButtons.map((page) => (
          <button
            key={page}
            onClick={() => handlePageChange(page)}
            className={currentPage === page ? 'active' : ''}
          >
            {page}
          </button>
        ))}
        <button
          onClick={() => handlePageChange(currentPage + 1)}
        >
          Next
        </button>
      </div>
    </div>
  );
}

export default AllPetsPage;
