import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import PetCard from './PetCard';
import '../styles/LocationSpecificPets.css';

const LocationSpecificPets = ({
  favoritePets,
  addToFavorites,
  removeFromFavorites
}) => {
  const location = useLocation();
  const state = location.state;

  // Define the currentPage state at the top level
  const [currentPage, setCurrentPage] = useState(1);
  const [petsToDisplay, setPetsToDisplay] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (!state || !state.data) {
      return; // Exit early if there's no data
    }

    // unpack the values received from the state
    const { data, petType, searchText } = state;
    const { total_pages } = data.pagination;

    const itemsPerPage = 20;

    // Function to fetch pets for the current page
    const fetchPetsForCurrentPage = async (page) => {
      setLoading(true);
      const startIndex = (page - 1) * itemsPerPage;
      const endIndex = startIndex + itemsPerPage;

      try {
        // Make an API call to fetch pets for the current page
        const response = await fetch(
          `http://localhost:3002/api/petfinder?type=${petType}&limit=${itemsPerPage}&page=${page}`
        );
        if (response.ok) {
          const result = await response.json();
          const pets = result.data.animals.slice(startIndex, endIndex);
          setPetsToDisplay(pets);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      }

      setLoading(false);
    };

    // Fetch pets for the initial page
    fetchPetsForCurrentPage(currentPage);
  }, [currentPage, state]);

  // Function to handle moving to the previous page
  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  // Function to handle moving to the next page
  const handleNextPage = () => {
    if (currentPage < state.data.pagination.total_pages) {
      setCurrentPage(currentPage + 1);
    }
  };

  return (
    <div className="location-specific-pets">
      <h2 className="search-results-title">Search Results for:</h2>
      <h3 className="pet-type-title">All {state.petType}s in the {state.searchText} area</h3>
      <div className="pet-card-container">
        {loading ? (
          <p className="loading-message">Loading...</p>
        ) : petsToDisplay.length === 0 ? (
          <p className="no-animals-message">No animals found.</p>
        ) : (
          petsToDisplay.map((pet) => (
            <PetCard
              key={pet.id}
              pet={pet}
              addToFavorites={addToFavorites}
              removeFromFavorites={removeFromFavorites}
              isFavorite={favoritePets.some((favoritePet) => favoritePet.id === pet.id)}
            />
          ))
        )}
      </div>
      <div className="pagination">
        <button onClick={handlePreviousPage} disabled={currentPage === 1}>
          Previous Page
        </button>
        <span>Page {currentPage} of {state.data.pagination.total_pages}</span>
        <button onClick={handleNextPage} disabled={currentPage === state.data.pagination.total_pages}>
          Next Page
        </button>
      </div>
    </div>
  );
};

export default LocationSpecificPets;
