import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import PetCard from './PetCard';
import '../styles/LocationSpecificPets.css';
import usePetfinderAPI from '../hooks/usePetFinderAPI';

const LocationSpecificPets = ({
  favoritePets,
  addToFavorites,
  removeFromFavorites
}) => {
  const location = useLocation();
  const state = location.state;

  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 20;

  if (!state || !state.data) {
    return <div className="loading">Loading...</div>;
  }

  const { data, petType, searchText } = state;
  const { total_pages } = data.pagination;

  const [fetchedAnimals, setFetchedAnimals] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchAnimalsForCurrentPage = async (category, page) => {
    setLoading(true);

    const animals = await usePetfinderAPI(
      `http://localhost:3002/api/petfinder?type=${category}&limit=${itemsPerPage}&page=${page}`
    );

    setFetchedAnimals((prevAnimals) => [...prevAnimals, ...animals]);

    setLoading(false);
  };

  useEffect(() => {
    if (currentPage <= total_pages) {
      fetchAnimalsForCurrentPage(petType, currentPage);
    }
  }, [currentPage, petType, total_pages]);

  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;

  const petsToDisplay = fetchedAnimals.slice(startIndex, endIndex);

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handleNextPage = () => {
    if (currentPage < total_pages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const buttonsToShow = 9;
  const startPage = Math.max(1, currentPage - Math.floor(buttonsToShow / 2));
  const endPage = startPage + buttonsToShow - 1;

  const pageButtons = Array.from({ length: buttonsToShow }, (_, i) => startPage + i);

  return (
    <div className="location-specific-pets">
      <h2 className="search-results-title">Search Results for:</h2>
      <h3 className="pet-type-title">All {petType}s in the {searchText} area</h3>
      <div className="pet-card-container">
        {petsToDisplay.length === 0 ? (
          <p className="no-animals-message">No animals found.</p>
        ) : (
          petsToDisplay.map((pet) => (
            <PetCard
              key={pet.id}
              pet={pet}
              addToFavorites={addToFavorites}
              removeFromFavorites={removeFromFavorites}
              isFavorite={favoritePets.some((favoritePet) => favoritePet.id === pet.id)}
            />
          ))
        )}
      </div>
      <div className="pagination">
        <button onClick={handlePreviousPage} disabled={currentPage === 1}>
          Previous Page
        </button>
        {pageButtons.map((page) => (
          <button
            key={page}
            onClick={() => setCurrentPage(page)}
            className={currentPage === page ? 'active' : ''}
          >
            {page}
          </button>
        ))}
        <button onClick={handleNextPage} disabled={currentPage === total_pages}>
          Next Page
        </button>
      </div>
    </div>
  );
};

export default LocationSpecificPets;
