import express from 'express';
import cors from 'cors';
import fetch from 'node-fetch';

const app = express();
const port = 3001;

app.use(cors());

// API credentials and endpoint
const apiKey = process.env.REACT_APP_PET_FINDER_API_KEY;
const apiSecret = process.env.REACT_APP_PET_FINDER_API_SECRET;
const petfinderEndpoint = 'https://api.petfinder.com/v2/animals';
const accessToken = process.env.REACT_APP_PET_FINDER_API_KEY;

app.get('/api/petfinder', async (req, res) => {
  const queryParams = new URLSearchParams(req.query);

  try {
    const response = await fetch(`${petfinderEndpoint}?${queryParams.toString()}`, {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${accessToken}`,
        'Content-Type': 'application/json',
      },
    });

    if (!response.ok) {
      const errorData = await response.json();
      console.error('API Error:', errorData);
      throw new Error(`Request failed with status: ${response.status}`);
    }

    const data = await response.json();
    res.json(data);
  } catch (error) {
    console.error('Error fetching data:', error);
    res.status(500).json({ error: 'An error occurred while fetching data' });
  }
});

app.listen(port, () => {
  console.log(`Server is running on port ${port}`);
});
