import React, { useState, useEffect } from 'react';
import '../styles/SearchBar.css';

const SearchBar = ({ onSearch }) => {
  const [searchText, setSearchText] = useState('');
  const [petType, setPetType] = useState('');
  const [showLocationOptions, setShowLocationOptions] = useState(false);

  const handleSearch = async () => {
    // Construct the API query URL with query parameters
    const apiUrl = 'https://api.example.com/animals'; // Replace with your API endpoint
    const queryParams = new URLSearchParams();
    if (searchText) {
      queryParams.append('location', searchText);
    }
    if (petType) {
      queryParams.append('type', petType);
    }
    // Add more query parameters as needed

    const apiEndpoint = `${apiUrl}?${queryParams.toString()}`;

    try {
      const response = await fetch(apiEndpoint);
      if (response.ok) {
        const data = await response.json();
        onSearch(data); // Pass the API response data to a callback function
      } else {
        console.error('API request failed:', response.statusText);
      }
    } catch (error) {
      console.error('API request error:', error);
    }
  };

  const handleShareLocation = async () => {
    // Add geolocation code here to get the user's current location

    // Example geolocation code
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(async (position) => {
        try {
          // Use position.coords.latitude and position.coords.longitude to get the user's location
          const latitude = position.coords.latitude;
          const longitude = position.coords.longitude;

          // Use the user's location to search for nearby animals
          setSearchText(`${latitude},${longitude}`);
        } catch (error) {
          console.error("Error getting location:", error);
        }
      }, (error) => {
        // Handle any errors
        alert(`Error getting location: ${error.message}`);
      });
    } else {
      alert("Geolocation is not supported in your browser");
    }
  };

  const handleInputClick = () => {
    setShowLocationOptions(!showLocationOptions);
  };

  const handleLocationOptionClick = () => {
    handleShareLocation();
    setShowLocationOptions(false);
  };

  return (
    <div className="search-bar">
      <div className="search-container">
        <div className="search-input-container">
          <input
            type="text"
            placeholder="Enter location, city, or state"
            value={searchText}
            onChange={(e) => setSearchText(e.target.value)}
            onClick={handleInputClick}
            className="search-input"
          />
          {showLocationOptions && (
            <div className="location-option" onClick={handleLocationOptionClick}>
              Share Location
            </div>
          )}
        </div>
        <select
          value={petType}
          onChange={(e) => setPetType(e.target.value)}
          className="search-select"
        >
          <option value="">Select pet type</option>
          <option value="dog">Dog</option>
          <option value="cat">Cat</option>
          <option value="horse">Horse</option>
          <option value="bird">Bird</option>
          <option value="furry">Furry</option>
          <option value="barnyard">Barnyard</option>
        </select>
        <button onClick={handleSearch} className="search-button">Search</button>
      </div>
    </div>
  );
};

export default SearchBar;
