import React, { useState } from 'react';
import '../styles/SearchBar.css';

const SearchBar = ({ onSearch }) => {
  const [searchText, setSearchText] = useState('');
  const [petType, setPetType] = useState('');
  const [showCustomDropdown, setShowCustomDropdown] = useState(false);

  const handleSearch = () => {
    const searchQuery = {
      text: searchText,
      type: petType,
    };
    onSearch(searchQuery);
  };

  const handleShareLocation = () => {
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition((position) => {
        // Use position.coords.latitude and position.coords.longitude to get the user's location
        // You can send this location data to the server for location-based searches
        alert(`Latitude: ${position.coords.latitude}, Longitude: ${position.coords.longitude}`);
      }, (error) => {
        // Handle any errors
        alert(`Error getting location: ${error.message}`);
      });
    } else {
      alert("Geolocation is not supported in your browser");
    }
  };

  const handleToggleCustomDropdown = () => {
    setShowCustomDropdown(!showCustomDropdown);
  };

  const handleSelectOption = (selectedOption) => {
    if (selectedOption === "shareLocation") {
      handleShareLocation();
    } else {
      setPetType(selectedOption);
    }
    setShowCustomDropdown(false);
  };

  return (
    <div className="search-bar">
      <div className="search-container">
        <input
          type="text"
          placeholder="Enter zipcode, city, or state"
          value={searchText}
          onChange={(e) => setSearchText(e.target.value)}
          className="search-input"
        />
        <div className="custom-select">
          <div
            className={`select-trigger ${showCustomDropdown ? 'active' : ''}`}
            onClick={handleToggleCustomDropdown}
          >
            {petType || 'Select pet type'}
          </div>
          <div className={`select-options ${showCustomDropdown ? 'active' : ''}`}>
            <div
              className="select-option"
              onClick={() => handleSelectOption('shareLocation')}
            >
              Share Location
            </div>
            <div
              className="select-option"
              onClick={() => handleSelectOption('dog')}
            >
              Dog
            </div>
            <div
              className="select-option"
              onClick={() => handleSelectOption('cat')}
            >
              Cat
            </div>
            <div
              className="select-option"
              onClick={() => handleSelectOption('horse')}
            >
              Horse
            </div>
            <div
              className="select-option"
              onClick={() => handleSelectOption('bird')}
            >
              Bird
            </div>
            <div
              className="select-option"
              onClick={() => handleSelectOption('furry')}
            >
              Furry
            </div>
            <div
              className="select-option"
              onClick={() => handleSelectOption('barnyard')}
            >
              Barnyard
            </div>
          </div>
        </div>
        <button onClick={handleSearch} className="search-button">Search</button>
      </div>
    </div>
  );
};

export default SearchBar;
