import React, { useState } from 'react';
import '../styles/SearchBar.css';

const SearchBar = ({ onSearch }) => {
  const [searchText, setSearchText] = useState('');
  const [petType, setPetType] = useState('');
  const [showLocationButton, setShowLocationButton] = useState(false);

  const handleSearch = () => {
    const searchQuery = {
      text: searchText,
      type: petType,
    };
    onSearch(searchQuery);
  };

  const handleShareLocation = () => {
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition((position) => {
        // Use position.coords.latitude and position.coords.longitude to get the user's location
        // You can send this location data to the server for location-based searches
        alert(`Latitude: ${position.coords.latitude}, Longitude: ${position.coords.longitude}`);
      }, (error) => {
        // Handle any errors
        alert(`Error getting location: ${error.message}`);
      });
    } else {
      alert("Geolocation is not supported in your browser");
    }
  };

  const handleInputFocus = () => {
    setShowLocationButton(true);
  };

  const handleInputBlur = () => {
    setShowLocationButton(false);
  };

  return (
    <div className="search-bar">
      <div className="search-container">
        <input
          type="text"
          placeholder="Enter zipcode, city, or state"
          value={searchText}
          onChange={(e) => setSearchText(e.target.value)}
          onFocus={handleInputFocus}
          onBlur={handleInputBlur}
          className="search-input"
        />
        {showLocationButton && (
          <button onClick={handleShareLocation} className="location-button">Share Location</button>
        )}
        <select
          value={petType}
          onChange={(e) => setPetType(e.target.value)}
          className="search-select"
        >
          <option value="">Select pet type</option>
          <option value="dog">Dog</option>
          <option value="cat">Cat</option>
          <option value="horse">Horse</option>
          <option value="bird">Bird</option>
          <option value="furry">Furry</option>
          <option value="barnyard">Barnyard</option>
        </select>
        <button onClick={handleSearch} className="search-button">Search</button>
      </div>
    </div>
  );
};

export default SearchBar;
