import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import PetCard from './PetCard';
import '../styles/LocationSpecificPets.css';

const LocationSpecificPets = ({
  favoritePets,
  addToFavorites,
  removeFromFavorites
}) => {
  const location = useLocation();
  const state = location.state;

  // Define the currentPage state at the top level
  const [currentPage, setCurrentPage] = useState(1);
  const [petsToDisplay, setPetsToDisplay] = useState([]);
  const [totalPages, setTotalPages] = useState(0);

  useEffect(() => {
    if (!state || !state.data) {
      return;
    }

    // unpack the values received from the state
    const { data, petType, searchText } = state;
    const { total_pages } = data.pagination;

    setTotalPages(total_pages);
  }, [state]);

  const petsPerPage = 20;

  // Function to handle moving to the previous page
  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  // Function to handle moving to the next page
  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  useEffect(() => {
    // Function to fetch data for the current page
    const fetchDataForCurrentPage = async () => {
      if (!state || !state.data) {
        return;
      }

      const { data } = state;
      const startIndex = (currentPage - 1) * petsPerPage;
      const endIndex = startIndex + petsPerPage;
      const petsToDisplay = data.animals.slice(startIndex, endIndex);

      setPetsToDisplay(petsToDisplay);
    };

    fetchDataForCurrentPage();
  }, [currentPage, state]);

  // Calculate the range of page buttons to display
  const buttonsToShow = 9;
  const startPage = Math.max(1, currentPage - Math.floor(buttonsToShow / 2));
  const endPage = startPage + buttonsToShow - 1;
  const pageButtons = Array.from({ length: buttonsToShow }, (_, i) => startPage + i);

  return (
    <div className="location-specific-pets">
      <h2 className="search-results-title">Search Results for:</h2>
      <h3 className="pet-type-title">All {state.petType}s in the {state.searchText} area</h3>
      <div className="pet-card-container">
        {petsToDisplay.length === 0 ? (
          <p className="no-animals-message">No animals found.</p>
        ) : (
          petsToDisplay.map((pet) => (
            <PetCard
              key={pet.id}
              pet={pet}
              addToFavorites={addToFavorites}
              removeFromFavorites={removeFromFavorites}
              isFavorite={favoritePets.some((favoritePet) => favoritePet.id === pet.id)}
            />
          ))
        )}
      </div>
      <div className="pagination">
        <button onClick={handlePreviousPage} disabled={currentPage === 1}>
          Previous Page
        </button>
        {pageButtons.map((page) => (
          <button
            key={page}
            onClick={() => setCurrentPage(page)}
            className={currentPage === page ? 'active' : ''}
          >
            {page}
          </button>
        ))}
        <button onClick={handleNextPage} disabled={currentPage === totalPages}>
          Next Page
        </button>
      </div>
    </div>
  );
};

export default LocationSpecificPets;
