import React, { useState, useEffect } from 'react';
import '../styles/MainPage.css';
import PetCard from './PetCard';
import Filter from './Filter';
import SampleData from './SampleData';

function MainPage({ onFilterChange, favoritePets, setFavoritePets, addToFavorites, removeFromFavorites }) {
  const initialFilters = {
    type: 'any',
    breed: 'any',
    age: 'any',
    gender: 'any',
    size: 'any',
    coat: 'any',
    tags: 'any',
    name: 'any',
    description: 'any',
    photos: 'any',
    status: 'any',
    contact: 'any',
  };

  const [filters, setFilters] = useState(initialFilters);
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // When the component mounts or filters change, filter the data
    fetchPetData(filters);
  }, [filters]);

  const fetchPetData = (filters) => {
    const apiKey = 'Cnw2cz4Mx9Vcs8GNUcXmnX0Q4nl1Q5Q8BEVjVukFoFHojNAloy';
    const apiSecret = 'TnFYeXeE51lVoXGjJX9buU3BOFgeb8TPoLGNohKX';
    const endpoint = 'https://api.petfinder.com/v2/animals';

    const queryParams = new URLSearchParams({
      type: filters.type,
      breed: filters.breed,
      age: filters.age,
      gender: filters.gender,
      size: filters.size,
      coat: filters.coat,
    });

    const credentials = `${apiKey}:${apiSecret}`;
    const base64Credentials = btoa(credentials);

    fetch(`${endpoint}?${queryParams.toString()}`, {
      method: 'GET',
      headers: {
        'Authorization': `Basic ${base64Credentials}`,
        'Content-Type': 'application/json',
      },
    })
    .then((response) => response.json())
    .then((data) => {
      console.log('API Response:', data);
      setSearchResults(data.animals || []);
      setLoading(false);
    })
    .catch((error) => {
      console.error('Error fetching data:', error);
      setLoading(false);
    });
  };

  const handleFilterChange = (filterName, value) => {
    const updatedFilters = { ...filters, [filterName]: value };
    setFilters(updatedFilters);
  };

  return (
    <div className="main-page">
      <div className="sidebar">
        <div className="filters">
          <Filter filters={filters} onFilterChange={handleFilterChange} />
        </div>
      </div>
      <div className="content">
        {loading ? (
          <p>Loading...</p>
        ) : (
          <div className="pet-card-list">
            {searchResults.map((pet) => (
              <PetCard
                key={pet.id}
                pet={pet}
                addToFavorites={addToFavorites}
                removeFromFavorites={removeFromFavorites}
                isFavorite={favoritePets.some((favoritePet) => favoritePet.id === pet.id)}
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default MainPage;
