// SearchFilters.js
import React from 'react';

function SearchFilters({ breeds, sizes, ages, types, onFilterChange }) {
  return (
    <div className="search-filters">
      {/* Search bar */}
      <input type="text" placeholder="Search by name, breed, or keyword..." />

      {/* City input */}
      <input type="text" id="cityInput" placeholder="City (optional)" />

      {/* State input */}
      <input type="text" id="stateInput" placeholder="State (optional)" />

      {/* Zip code input */}
      <input type="text" id="zipCodeInput" placeholder="Zip Code (optional)" />

      {/* Type of pet select */}
      <select onChange={onFilterChange}>
        <option value="">All Types</option>
        {types.map(type => (
          <option key={type} value={type}>{type}</option>
        ))}
      </select>

      {/* More filters */}
    </div>
  );
}

export default SearchFilters;
