import React, { useState, useEffect, useCallback } from 'react';
import '../styles/MainPage.css';
import PetCard from './PetCard';
import Filter from './Filter';

function MainPage({ favoritePets, addToFavorites, removeFromFavorites }) {
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1); // Initialize with page 1
  const itemsPerPage = 10; // Number of dogs to display per page

  const [filters, setFilters] = useState({
    type: 'any',
    breed: 'any',
    age: 'any',
    gender: 'any',
    size: 'any',
    coat: 'any',
  });

  const fetchAllPets = useCallback(() => {
    const endpoint = `http://localhost:3002/api/petfinder?page=${currentPage}&perPage=${itemsPerPage}`;

    fetch(endpoint)
      .then((response) => response.json())
      .then((data) => {
        console.log('API Data:', data);
        setSearchResults(data.animals || []);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching data:', error.message);
        setLoading(false);
      });
  }, [currentPage, itemsPerPage]);

  useEffect(() => {
    // When the component mounts or filters change, fetch all animals
    fetchAllPets();
  }, [fetchAllPets, filters]);

  const applyFilters = (data) => {
    // Apply filters for all filter categories
    for (const filterKey in filters) {
      const filterValue = filters[filterKey]?.toLowerCase();
      if (filterValue && filterValue !== 'any') {
        data = data.filter((pet) => {
          // Filter logic for the given category
          const petValue = pet[filterKey]?.toLowerCase();
          return petValue && petValue === filterValue;
        });
      }
    }

    // Slice the data based on the current page and items per page
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    return data.slice(startIndex, endIndex);
  };

  const handleNextPage = () => {
    // Increment the current page and fetch data for the next page
    setCurrentPage(currentPage + 1);
  };

  const handlePrevPage = () => {
    if (currentPage > 1) {
      // Decrement the current page (if not on the first page)
      setCurrentPage(currentPage - 1);
    }
  };

  const handleFilterChange = (selectedFilters) => {
    // Update the filters state with the selected filter values
    setFilters(selectedFilters);
    // Reset the current page to 1 when filters change
    setCurrentPage(1);
  };

  const filteredResults = applyFilters(searchResults);

  return (
    <div className="main-page">
      <div className="sidebar">
        <div className="filters">
          <Filter filters={filters} onFilterChange={handleFilterChange} />
        </div>
      </div>
      <div className="content">
        <div className="pagination">
          <button onClick={handlePrevPage} disabled={currentPage === 1}>
            Previous Page
          </button>
          <button onClick={handleNextPage} disabled={filteredResults.length < itemsPerPage}>
            Next Page
          </button>
        </div>
        {loading ? (
          <p>Loading...</p>
        ) : (
          <div className="pet-card-list">
            {filteredResults.map((pet) => (
              <PetCard
                key={pet.id}
                pet={pet}
                addToFavorites={addToFavorites}
                removeFromFavorites={removeFromFavorites}
                isFavorite={favoritePets.some((favoritePet) => favoritePet.id === pet.id)}
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default MainPage;
