import React, { useState, useEffect } from 'react';
import '../styles/MainPage.css';
import SearchBar from './SearchBar';
import PetCard from './PetCard';
import Filter from './Filter';
import SampleData from './SampleData'

function MainPage({ onFilterChange }) {
  const [filters, setFilters] = useState({
    size: 'any',
    breed: 'any',
    age: 'any',
    gender: 'any',
    shelter: 'any',
    petType: 'any', // Add petType filter
  });

  const [searchResults, setSearchResults] = useState([]); // Initialize searchResults state

  useEffect(() => {
    // Fetch and populate data from your API here and set it in setSearchResults
    // For now, using sampleData for demonstration
    setSearchResults(SampleData);
  }, []);

  const filterPets = () => {
    console.log('Filters:', filters);
    // Initialize filteredData with the full dataset
    let filteredData = [...SampleData];

    // Filter by size
    if (filters.size !== 'any') {
      const filterSize = filters.size.toLowerCase(); // Convert filter value to lowercase
      filteredData = filteredData.filter((pet) => pet.size.toLowerCase() === filterSize);
    }

    // Filter by breed
    if (filters.breed !== 'any') {
      const filterBreed = filters.breed.toLowerCase(); // Convert filter value to lowercase
      filteredData = filteredData.filter((pet) => pet.breed.toLowerCase() === filterBreed);
    }

    // Filter by age
    if (filters.age !== 'any') {
      const filterAge = filters.age.toLowerCase(); // Convert filter value to lowercase
      filteredData = filteredData.filter((pet) => pet.age.toLowerCase() === filterAge);
    }

    // Filter by gender
    if (filters.gender !== 'any') {
      const filterGender = filters.gender.toLowerCase(); // Convert filter value to lowercase
      filteredData = filteredData.filter((pet) => pet.gender.toLowerCase() === filterGender);
    }

    // Filter by shelter
    if (filters.shelter !== 'any') {
      const filterShelter = filters.shelter.toLowerCase(); // Convert filter value to lowercase
      filteredData = filteredData.filter((pet) => pet.shelter.toLowerCase() === filterShelter);
    }
    console.log('Filtered Data:', filteredData);

    // Update searchResults with the filtered data
    setSearchResults(filteredData);
  };

  const handleFilterChange = (filterName, value) => {
    setFilters({
      ...filters,
      [filterName]: value,
    });

    // Call the filterPets function to apply the filters immediately
    filterPets();
  };

  return (
    <div className="main-page">
      <div className="search-bar">
        <SearchBar />
      </div>
      <div className="sidebar">
        <div className="filters">
          <Filter filters={filters} onFilterChange={handleFilterChange} />
        </div>
      </div>
      <div className="content">
        <div className="pet-card-list">
          {searchResults.map((pet) => (
            <PetCard key={pet.id} pet={pet} />
          ))}
        </div>
      </div>
    </div>
  );
}

export default MainPage;
