import React, { useState, useEffect, useCallback, useRef } from 'react';
import '../styles/MainPage.css';
import PetCard from './PetCard';
import Filter from './Filter';

function MainPage({ favoritePets, addToFavorites, removeFromFavorites }) {
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10;
  const minimumPets = 100;
  const fetchInProgress = useRef(false);
  const initialLoad = useRef(true); // Track the initial load

  const [filters, setFilters] = useState({
    type: 'any',
    breed: 'any',
    age: 'any',
    gender: 'any',
    size: 'any',
    coat: 'any',
  });

  const fetchAllPets = useCallback(() => {
    if (fetchInProgress.current || searchResults.length >= minimumPets) {
      return;
    }

    fetchInProgress.current = true;

    const endpoint = `http://localhost:3002/api/petfinder?page=${currentPage}&perPage=${itemsPerPage}`;

    fetch(endpoint)
      .then((response) => response.json())
      .then((data) => {
        console.log('API Data:', data);

        const updatedResults = [...searchResults, ...(data.animals || [])];
        setSearchResults(updatedResults);

        if (updatedResults.length < minimumPets) {
          setCurrentPage(currentPage + 1);
        } else {
          setLoading(false);
        }
      })
      .catch((error) => {
        console.error('Error fetching data:', error.message);
        setLoading(false);
      })
      .finally(() => {
        fetchInProgress.current = false;
      });
  }, [currentPage, itemsPerPage, minimumPets, searchResults]);

  useEffect(() => {
    if (!initialLoad.current) {
      // When filters change and it's not the initial load, reset the state and fetch data
      setSearchResults([]);
      setCurrentPage(1);
      setLoading(true);
      fetchAllPets();
    }
  }, [fetchAllPets, filters]);

  useEffect(() => {
    // Check if it's the initial load, if so, set it to false
    initialLoad.current = false;

    if (filters.type !== 'any') {
      setLoading(true);
      fetchAllPets();
    }
  }, [fetchAllPets, filters.type]);

  const applyFilters = (data) => {
    for (const filterKey in filters) {
      const filterValue = filters[filterKey]?.toLowerCase();
      if (filterValue && filterValue !== 'any') {
        data = data.filter((pet) => {
          const petValue = pet[filterKey]?.toLowerCase();
          return petValue && petValue === filterValue;
        });
      }
    }

    return data;
  };

  const handleFilterChange = (selectedFilters) => {
    setFilters(selectedFilters);
  };

  const filteredResults = applyFilters(searchResults);

  return (
    <div className="main-page">
      <div className="sidebar">
        <div className="filters">
          <Filter filters={filters} onFilterChange={handleFilterChange} />
        </div>
      </div>
      <div className="content">
        {loading ? (
          <p>Loading...</p>
        ) : (
          <div className="pet-card-list">
            {filteredResults.map((pet) => (
              <PetCard
                key={pet.id}
                pet={pet}
                addToFavorites={addToFavorites}
                removeFromFavorites={removeFromFavorites}
                isFavorite={favoritePets.some((favoritePet) => favoritePet.id === pet.id)}
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default MainPage;
