import React, { useState } from 'react';
import '../styles/PetCard.css';
import { useNavigate } from 'react-router-dom';

const PetCard = ({ pet, addToFavorites, removeFromFavorites, isFavorite }) => {
  const [favorited, setFavorited] = useState(isFavorite);
  const navigate = useNavigate();

  // console.log('PetCard Data:', pet);

  const handleToggleFavorite = () => {
    setFavorited(!favorited);
    if (favorited) {
      removeFromFavorites(pet.id);
    } else {
      addToFavorites(pet);
    }
  };

  const handleMoreInfoClick = () => {
    navigate(`/pet-details/${pet.id}`, { state: { petData: pet } });
  };

  if (!pet.photos || pet.photos.length === 0) {
    // Display a fallback photo or message
    return (
      <div className="pet-card">
        <img src="/path/to/fallback-photo.jpg" alt={pet.name} className="pet-card-image" />
        {/* Other pet information */}
      </div>
    );
  }

  if (!pet.description || pet.description.length === 0) {
    // Display a message indicating no description
    return (
      <div className="pet-card">
        <img src={pet.photos[0]?.medium} alt={pet.name} className="pet-card-image" />
        <h4>
          {pet.name.length > 9 ? pet.name.substring(0, 9) + ' ...' : pet.name}{' '}
          <span role="img" aria-label="Location">
            üìç{truncatedAddress}, {pet.contact.address.state}
          </span>
        </h4>
        <div className="pet-card-info">
          <div>
            <p>
              {pet.age} | {pet.gender} | {pet.size} | {pet.breeds.primary}
            </p>
          </div>
        </div>
        <p className="pet-card-description">This pet doesn't have a description.</p>
        {/* Rest of the pet information */}
      </div>
    );
  }}
