import React, { useState, useEffect } from 'react';
import popularDogBreeds from './popularDogBreeds';

function Filter({ filters, onFilterChange }) {
  // Create state variables for filter values
  const [selectedType, setSelectedType] = useState(filters.type || 'any');
  const [selectedBreed, setSelectedBreed] = useState(filters.breed || 'any');
  const [selectedAge, setSelectedAge] = useState(filters.age || 'any');
  const [selectedGender, setSelectedGender] = useState(filters.gender || 'any');
  const [selectedSize, setSelectedSize] = useState(filters.size || 'any');
  const [selectedCoat, setSelectedCoat] = useState(filters.coat || 'any');

  // Handle filter selection and update state variables
  const handleFilterSelect = (filterName, selectedValue) => {
    switch (filterName) {
      case 'type':
        setSelectedType(selectedValue);
        setSelectedBreed('any'); // Reset breed when type changes
        setSelectedAge('any'); // Reset age when type changes
        setSelectedGender('any'); // Reset gender when type changes
        setSelectedSize('any'); // Reset size when type changes
        setSelectedCoat('any'); // Reset coat when type changes
        break;
      case 'breed':
        setSelectedBreed(selectedValue);
        break;
      case 'age':
        setSelectedAge(selectedValue);
        break;
      case 'gender':
        setSelectedGender(selectedValue);
        break;
      case 'size':
        setSelectedSize(selectedValue);
        break;
      case 'coat':
        setSelectedCoat(selectedValue);
        break;
      default:
        break;
    }

    // Update parent component with the selected filter
    onFilterChange(filterName, selectedValue);
  };

  // Render dog-specific filters
  const renderDogFilters = () => {
    return (
      <>
        {/* Dog-specific filters */}
      </>
    );
  };

  // Render cat-specific filters
  const renderCatFilters = () => {
    return (
      <>
        {/* Cat-specific filters */}
      </>
    );
  };

  return (
    <div className="filters">
      <h2>Filters</h2>
      <div className="filter-group">
        <label htmlFor="type">Type:</label>
        <select
          id="type"
          value={selectedType}
          onChange={(event) => handleFilterSelect('type', event.target.value)}
        >
          <option value="any">Any</option>
          <option value="dog">Dog</option>
          <option value="cat">Cat</option>
        </select>
      </div>
      {/* Render dog filters if type is "dog" */}
      {selectedType.toLowerCase() === 'dog' && renderDogFilters()}
      {/* Render cat filters if type is "cat" */}
      {selectedType.toLowerCase() === 'cat' && renderCatFilters()}
    </div>
  );
}

export default Filter;
