import React, { useState } from 'react';
import '../styles/MainPage.css';
import SearchBar from './SearchBar';
import PetCard from './PetCard';
import Filter from './Filter';

function MainPage({ onFilterChange }) {
  const [filters, setFilters] = useState({
    size: 'any',
    breed: 'any',
    age: 'any',
    gender: 'male',
    shelter: 'shelter1',
  });

  // Sample pet data
   // Sample pet data
   const sampleData = [
    {
      id: 1,
      name: 'Buddy',
      breed: 'Golden Retriever',
      age: 'Puppy',
      size: 'Medium',
      gender: 'Male',
      shelter: 'Happy Paws Shelter',
      photo: '/images/dog2.jpg',
      distance: '2 miles',
    },
    {
      id: 2,
      name: 'Daisy',
      breed: 'Labrador',
      age: 'Adult',
      size: 'Large',
      gender: 'Female',
      shelter: 'Loving Hearts Rescue',
      photo: '/images/dog.jpg',
      distance: '5 miles',
    },
    {
      id: 3,
      name: 'Rex',
      breed: 'German Shepherd',
      age: 'Adult',
      size: 'Large',
      gender: 'Male',
      shelter: 'Pawsome Haven',
      photo: '/images/dog3.jpg',
      distance: '3 miles',
    },
    // Add more sample pet data as needed
  ];

  const [searchResults, setSearchResults] = useState(sampleData); // Initialize searchResults state

  const filterPets = () => {
    // Implement filter logic based on filters state
    const filteredData = sampleData.filter((pet) => {
      return (
        pet.size === filters.size &&
        pet.breed === filters.breed &&
        pet.age === filters.age &&
        pet.gender === filters.gender &&
        pet.shelter === filters.shelter
      );
    });

    // Update searchResults with the filtered data
    setSearchResults(filteredData);
  };

  const handleFilterChange = (filterName, value) => {
    setFilters({
      ...filters,
      [filterName]: value,
    });

    // Call the filterPets function to apply the filters immediately
    filterPets();
  };

  return (
    <div className="main-page">
      <div className="search-bar">
        <SearchBar />
      </div>
      <div className="sidebar">
        <div className="filters">
          <Filter filters={filters} onFilterChange={handleFilterChange} />
        </div>
      </div>
      <div className="content">
        <div className="pet-card-list">
          {searchResults.map((pet) => (
            <PetCard key={pet.id} pet={pet} />
          ))}
        </div>
      </div>
    </div>
  );
}

export default MainPage;
