import React, { useState, useEffect } from 'react';
import '../styles/MainPage.css';
import SearchBar from './SearchBar';
import PetCard from './PetCard';
import Filter from './Filter';
import SampleData from './SampleData';

function MainPage() {
  // Separate state variables for each filter category
  const [petType, setPetType] = useState('any');
  const [breed, setBreed] = useState('any');
  const [age, setAge] = useState('any');

  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {

    setSearchResults(SampleData);
  }, []);

  const filterPets = () => {
    let filteredData = [...SampleData];

    // Filter by pet type
    if (petType !== 'any') {
      filteredData = filteredData.filter((pet) => pet.type.toLowerCase() === petType.toLowerCase());
    }
    // Filter by age
    if (age !== 'any') {
      filteredData = filteredData.filter((pet) => pet.age.toLowerCase() === age);
    }
    // Add more filter conditions for other filter categories

    setSearchResults(filteredData);
  };

  const handleFilterChange = (filterName, value) => {
    const lowercasedValue = value ? value.toLowerCase() : 'any';

    // Pass the new filter values directly to filterPets
    filterPets({ ...filters, [filterName]: lowercasedValue });
  };

  return (
    <div className="main-page">
      {/* <div className="search-bar">
        <SearchBar />
      </div> */}
      <div className="sidebar">
        <div className="filters">
          <Filter filters={filters} onFilterChange={handleFilterChange} />
        </div>
      </div>
      <div className="content">
        <div className="pet-card-list">
          {searchResults.map((pet) => (
            <PetCard key={pet.id} pet={pet} />
          ))}
        </div>
      </div>
    </div>
  );
}

export default MainPage;
