import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/AllPetsPage.css';
import PetCard from './PetCard';

function shuffleArray(array) {
  // Shuffle array logic (as in your original code)
}

function AllPetsPage() {
  const { category } = useParams();
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [cache, setCache] = useState([]);
  const petsPerPage = 20;

  const fetchAnimalsByCategory = async (category, page) => {
    let endpoint;

    // Build the endpoint based on the category and page
    // Modify the endpoint as needed based on your API
    // Example: endpoint = `http://localhost:3002/api/petfinder?type=${category}&limit=${petsPerPage}&page=${page}`;

    try {
      // Make the API request using the endpoint
      const response = await fetch(endpoint);
      const data = await response.json();
      if (data && data.animals) {
        setCache(data.animals);
        setLoading(false);
      }
    } catch (error) {
      console.error('Error fetching data:', error.message);
    }
  };

  useEffect(() => {
    async function fetchData() {
      await fetchAnimalsByCategory(category, currentPage);
      // Scroll to the top when loading new animals
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }

    fetchData();
  }, [category, currentPage]);

  const handlePageChange = (newPage) => {
    if (newPage >= 1) {
      setCurrentPage(newPage);
    }
  };

  return (
    <div className="all-pets-page">
      <h2>{`All ${category.charAt(0).toUpperCase() + category.slice(1)}s`}</h2>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="pet-list">
          {cache.map((pet) => (
            <PetCard key={pet.id} pet={pet} />
          ))}
        </div>
      )}
      <div className="pagination">
        <button
          onClick={() => handlePageChange(currentPage - 1)}
          disabled={currentPage === 1}
        >
          Previous
        </button>
        {/* Display pagination buttons similar to the LocationSpecificPets component */}
        <span>Page {currentPage} of TotalPages</span>
        <button
          onClick={() => handlePageChange(currentPage + 1)}
          // Adjust the condition to disable the button for the last page
          // For example: disabled={currentPage === totalPages}
        >
          Next
        </button>
      </div>
    </div>
  );
}

export default AllPetsPage;
