// SearchFilters.js
import React from 'react';
import '../styles/Searc.css'; 
function SearchFilters({ breeds, sizes, ages, types, onFilterChange }) {
  return (
    <div className="search-filters">
      <div className="pet-type-section">
        <h2>Find your new pet</h2>
        {/* Type of pet select */}
        <select onChange={onFilterChange}>
          <option value="">All Types</option>
          {types.map(type => (
            <option key={type} value={type}>{type}</option>
          ))}
        </select>
      </div>

      <div className="location-section">
        <h2>Find pets near you</h2>
        {/* City input */}
        <input type="text" id="cityInput" placeholder="City" />

        {/* State input */}
        <input type="text" id="stateInput" placeholder="State" />

        {/* Zip code input */}
        <input type="text" id="zipCodeInput" placeholder="Zip Code" />

        <button className="search-button">Search</button>
      </div>
    </div>
  );
}

export default SearchFilters;
