import React, { useState, useEffect } from 'react';
import '../styles/MainPage.css';
import PetCard from './PetCard';
import Filter from './Filter';

function MainPage({ favoritePets, addToFavorites, removeFromFavorites }) {
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(true);

  const [filters, setFilters] = useState({
    type: 'any',
    breed: 'any',
    age: 'any',
    gender: 'any',
    size: 'any',
    coat: 'any',
  });

  useEffect(() => {
    // When the component mounts, fetch all animals
    fetchAllPets();
  }, []);

  const fetchAllPets = () => {
    const endpoint = 'http://localhost:3002/api/petfinder';

    fetch(endpoint)
      .then((response) => response.json())
      .then((data) => {
        console.log('API Data:', data);
        setSearchResults(data.animals || []);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching data:', error.message);
        setLoading(false);
      });
  };

  const applyFilters = (data) => {
    // Apply client-side filtering based on filters
    console.log('Filters:', filters); // Log filter values
    console.log('Data:', data); // Log the fetched data

    return data.filter((pet) => {
      if (!pet) {
        return false; // Skip undefined or null pets
      }

      const petType = pet.type ? pet.type.toLowerCase() : '';
      const petBreed = pet.breed || '';
      const petAge = pet.age || '';
      const petGender = pet.gender || '';
      const petSize = pet.size || '';
      const petCoat = pet.coat || '';

      return (
        (filters.type === 'any' || petType === filters.type.toLowerCase()) &&
        (filters.breed === 'any' || petBreed === filters.breed) &&
        (filters.age === 'any' || petAge === filters.age) &&
        (filters.gender === 'any' || petGender === filters.gender) &&
        (filters.size === 'any' || petSize === filters.size) &&
        (filters.coat === 'any' || petCoat === filters.coat)
      );
    });
  };

  const handleFilterChange = (selectedFilters) => {
    // Update the filters state with the selected filter values
    console.log(selectedFilters)
    setFilters(selectedFilters);
  };
  console.log(searchResults, "searchresults")
  const filteredResults = applyFilters(searchResults);

  return (
    <div className="main-page">
      <div className="sidebar">
        <div className="filters">
          <Filter filters={filters} onFilterChange={handleFilterChange} />
        </div>
      </div>
      <div className="content">
        {loading ? (
          <p>Loading...</p>
        ) : (
          <div className="pet-card-list">
            {filteredResults.map((pet) => (
              <PetCard
                key={pet.id}
                pet={pet}
                addToFavorites={addToFavorites}
                removeFromFavorites={removeFromFavorites}
                isFavorite={favoritePets.some((favoritePet) => favoritePet.id === pet.id)}
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default MainPage;
