import React, { useState, useEffect } from 'react';
import '../styles/MainPage.css';
import PetCard from './PetCard';
import Filter from './Filter';

function MainPage({ favoritePets, addToFavorites, removeFromFavorites }) {
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1); // Initialize with page 1
  const itemsPerPage = 10; // Number of dogs to display per page


  const [filters, setFilters] = useState({
    type: 'any',
    breed: 'any',
    age: 'any',
    gender: 'any',
    size: 'any',
    coat: 'any',
  });

  useEffect(() => {
    // When the component mounts, fetch all animals
    fetchAllPets();
  }, []);

  const fetchAllPets = () => {
    const endpoint = `http://localhost:3002/api/petfinder?page=${currentPage}&perPage=${itemsPerPage}`;

    fetch(endpoint)
      .then((response) => response.json())
      .then((data) => {
        console.log('API Data:', data);
        setSearchResults(data.animals || []);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching data:', error.message);
        setLoading(false);
      });
  };

  const applyFilters = (data, selectedFilters) => {
  // Initialize the filteredData with all data
  let filteredData = data;

  // Define a helper function for filter logic
  const applyFilterCategory = (filterKey) => {
    const filterValue = selectedFilters[filterKey]?.toLowerCase();
    if (filterValue && filterValue !== 'any') {
      filteredData = filteredData.filter((pet) => {
        // Filter logic for the given category
        const petValue = pet[filterKey]?.toLowerCase();
        return petValue && petValue === filterValue;
      });
    }
    // Slice the data based on the current page and items per page
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    return filteredData.slice(startIndex, endIndex);
  };

  // Apply filters for all filter categories
  for (const filterKey in selectedFilters) {
    applyFilterCategory(filterKey);
  }

  return filteredData;
};

  const handleFilterChange = (selectedFilters) => {
    // Convert all selected filter values to lowercase
    const lowercaseSelectedFilters = {};
    for (const filterKey in selectedFilters) {
      lowercaseSelectedFilters[filterKey] = selectedFilters[filterKey]?.toLowerCase();
    }

    // Update the filters state with the selected filter values
    setFilters(lowercaseSelectedFilters);
    console.log('Selected Type Filter:', lowercaseSelectedFilters);

    // Apply filters based on the selected filters
    const filteredResults = applyFilters(searchResults, lowercaseSelectedFilters);
    setSearchResults(filteredResults); // Update the filtered results
  };

  const handleNextPage = () => {
    // Increment the current page and fetch data for the next page
    setCurrentPage(currentPage + 1);
    fetchAllPets();
  };

  const handlePrevPage = () => {
    if (currentPage > 1) {
      // Decrement the current page (if not on the first page) and fetch data for the previous page
      setCurrentPage(currentPage - 1);
      fetchAllPets();
    }
  };


  const filteredResults = applyFilters(searchResults);

  return (
    <div className="main-page">
      <div className="sidebar">
        <div className="filters">
          <Filter filters={filters} onFilterChange={handleFilterChange} />
        </div>
      </div>
      <div className="content">
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="pet-card-list">
          {filteredResults.map((pet) => (
            <PetCard
              key={pet.id}
              pet={pet}
              addToFavorites={addToFavorites}
              removeFromFavorites={removeFromFavorites}
              isFavorite={favoritePets.some((favoritePet) => favoritePet.id === pet.id)}
            />
          ))}
        </div>
      )}
      </div>
    </div>
  );
}

export default MainPage;
