import React, { useState, useEffect, useRef, useCallback } from 'react';
import '../styles/MainPage.css';
import PetCard from './PetCard';
import Filter from './Filter';

function MainPage({ favoritePets, addToFavorites, removeFromFavorites }) {
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(true);
  const currentPageRef = useRef(1);
  const itemsPerPage = 10;
  const minimumPets = 100;
  const fetchInProgress = useRef(false);

  const [selectedFilters, setSelectedFilters] = useState({
    type: 'any',
    breed: 'any',
    age: 'any',
    gender: 'any',
    size: 'any',
    coat: 'any',
  });

  const fetchAllPets = (filters) => {
    if (fetchInProgress.current || searchResults.length >= minimumPets) {
      return;
    }

    fetchInProgress.current = true;

    // Include the filters in the API endpoint
    const endpoint = `http://localhost:3002/api/petfinder?page=${currentPageRef.current}&perPage=${itemsPerPage}&filters=${JSON.stringify(filters)}`;

    fetch(endpoint)
      .then((response) => response.json())
      .then((data) => {
        console.log('API Data:', data);

        const updatedResults = [...searchResults, ...(data.animals || [])];

        setLoading(false);

        if (updatedResults.length < minimumPets) {
          currentPageRef.current++;
        }

        setSearchResults(updatedResults);
      })
      .catch((error) => {
        console.error('Error fetching data:', error.message);
        setLoading(false);
      })
      .finally(() => {
        fetchInProgress.current = false;
      });
  };

  useEffect(() => {
    // Fetch all data on component mount
    fetchAllPets();
  }, []);

  // Function to apply filters
  const applyFilters = (data, filters) => {
    return data.filter((pet) => {
      for (const filterKey in filters) {
        const filterValue = filters[filterKey]?.toLowerCase();
        if (filterValue && filterValue !== 'any') {
          const petValue = pet[filterKey]?.toLowerCase();
          if (petValue !== filterValue) {
            return false;
          }
        }
      }
      return true;
    });
  };

  /// Function to handle the search based on selected filters
  const handleSearchClick = (filters) => {
    // Apply filters to the full list of pets
    const filteredResults = applyFilters(searchResults, filters);
    console.log('Search clicked with filters:', filters);
    setSearchResults(filteredResults);
  };
  const handleFilterChange = (newFilters) => {
    setSelectedFilters(newFilters);

    // Make a new API call with the updated filters
    fetchAllPets(newFilters);
  };

  // Render pet cards based on the filtered search results
  const renderPetCards = () => {
    if (!loading) {
      if (searchResults.length > 0) {
        return searchResults.map((pet) => (
          <PetCard
            key={pet.id}
            pet={pet}
            addToFavorites={addToFavorites}
            removeFromFavorites={removeFromFavorites}
            isFavorite={favoritePets.some((favoritePet) => favoritePet.id === pet.id)}
          />
        ));
      } else {
        return <p>No pets match your criteria.</p>;
      }
    } else {
      return <p>Loading...</p>;
    }
  };

  return (
    <div className="main-page">
      <div className="sidebar">
        <div className="filters">
          {/* Pass the handleSearchClick function to the Filter component */}
          <Filter onSearchClick={handleSearchClick} />
        </div>
      </div>
      <div className="content">
        {/* Render pet cards based on filtered search results */}
        <div className="pet-card-list">{renderPetCards()}</div>
      </div>
    </div>
  );
}

export default MainPage;
