import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // Import useParams to access route parameters
import '../styles/AllPetsPage.css';

function AllPetsPage() {
  const { category } = useParams(); // Use the useParams hook to get the category from the route parameter
  const [pets, setPets] = useState([]);
  const [loading, setLoading] = useState(true);

  // Reusing the fetchAllPets function
  const fetchAllPets = async () => {
    try {
      const endpoint = 'http://localhost:3002/api/petfinder?perPage=200';
      const response = await fetch(endpoint);
      const data = await response.json();

      console.log('API Response:', data);

      if (data && data.animals) {
        setPets(data.animals);
        setLoading(false);
      }
    } catch (error) {
      console.error('Error fetching data:', error.message);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAllPets(); // Fetch all pets when the component mounts
  }, []);

  return (
    <div className="all-pets-page">
      <h2>{`All ${category.charAt(0).toUpperCase() + category.slice(1)}`}</h2>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="pet-list">
          {pets.map((pet) => (
            <div key={pet.id}>
              <h3>{pet.name}</h3>
              {/* Display other pet information */}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default AllPetsPage;
