import React, { useState } from 'react';

const PetCard = ({ pet }) => {
  const [favorited, setFavorited] = useState(pet.favorited);

  const handleToggleFavorite = () => {
    setFavorited(!favorited);
  };

  return (
       <div className="pet-card">
      <div className="pet-card-header">
        <h3>{pet.name}</h3>
        <p>{pet.breed}</p>
      </div>
      <div className="pet-card-info">
        <div>
          <p>Age: {pet.age}</p>
          <p>Distance: {pet.distance} miles</p>
          <p>Gender: {pet.gender}</p>
          <p>Size: {pet.size}</p>
        </div>
        <div>
          <p>Description:</p>
          <p>{pet.description.substring(0, pet.description.indexOf('\n'))}</p>
          <p>{pet.description.substring(pet.description.indexOf('\n') + 1)}</p>
        </div>
        <div>
          <p>Shelter: {pet.shelterName}</p>
        </div>
      </div>
    </div>
  );
};

export default PetCard;
