import React, { useState } from 'react';
import popularDogBreeds from './popularDogBreeds';

function Filter({ onSearchClick, selectedFilters }) {
  const [localFilters, setLocalFilters] = useState(selectedFilters); // Use local state for filters

  const handleFilterSelect = (filterName, event) => {
    const selectedValue = event.target.value;

    setLocalFilters((prevFilters) => ({
      ...prevFilters,
      [filterName]: selectedValue,
    }));
  };

  const handleSearchClick = () => {
    // Pass the local filters to the parent component to trigger the search
    onSearchClick(localFilters);
  };

  const handleAnyFilterSelect = (filterName) => {
    // Reset the filter to "any" when "Any" is selected
    setLocalFilters((prevFilters) => ({
      ...prevFilters,
      [filterName]: 'any',
    }));
  };

  const renderDogFilters = () => {
    return (
      <>
        {/* Other filter options */}
        <div className="filter-group">
          <label htmlFor="breed">Breed:</label>
          <select
            id="breed"
            value={localFilters.breed}
            onChange={(event) => handleFilterSelect('breed', event)}
          >
            <option value="any" onClick={() => handleAnyFilterSelect('breed')}>
              Any
            </option>
            {/* Other breed options */}
          </select>
        </div>
        {/* Other dog filter groups */}
      </>
    );
  };

  const renderCatFilters = () => {
    return (
      <>
        {/* Other filter options */}
        <div className="filter-group">
          <label htmlFor="age">Age:</label>
          <select
            id="age"
            value={localFilters.age}
            onChange={(event) => handleFilterSelect('age', event)}
          >
            <option value="any" onClick={() => handleAnyFilterSelect('age')}>
              Any
            </option>
            {/* Other age options */}
          </select>
        </div>
        {/* Other cat filter groups */}
      </>
    );
  };

  return (
    <div className="filters">
      <h2>Filters</h2>
      <div className="filter-group">
        <label htmlFor="type">Type:</label>
        <select
          id="type"
          value={localFilters.type}
          onChange={(event) => handleFilterSelect('type', event)}
        >
          <option value="any" onClick={() => handleAnyFilterSelect('type')}>
            Any
          </option>
          <option value="dog">Dog</option>
          <option value="cat">Cat</option>
        </select>
      </div>
      {/* Render dog filters if type is "dog" */}
      {localFilters.type?.toLowerCase() === 'dog' && renderDogFilters()}
      {/* Render cat filters if type is "cat" */}
      {localFilters.type?.toLowerCase() === 'cat' && renderCatFilters()}

      <button onClick={handleSearchClick}>Search</button>
    </div>
  );
}

export default Filter;
