import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import PetCard from './PetCard';
import '../styles/LocationSpecificPets.css';

function shuffleArray(array) {
  // Function to shuffle an array
  for (let i = array.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [array[i], array[j]] = [array[j], array[i]];
  }
}

const LocationSpecificPets = ({
  favoritePets,
  addToFavorites,
  removeFromFavorites
}) => {
  const location = useLocation();
  const state = location.state;

  // Define the currentPage state
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 20; // Define the number of items per page

  if (!state || !state.data) {
    return <div className="loading">Loading...</div>;
  }

  const { data, petType, searchText } = state;
  const { total_pages } = data.pagination;

  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;

  // Function to fetch and filter pets by category and page
  const fetchAnimalsByCategory = async (page) => {
    let endpoint;
    let animalData;

    if (petType === "dog" || petType === "cat") {
      endpoint = `http://localhost:3002/api/petfinder?type=${petType}&limit=40&page=${page}`;
    } else if (petType === "other") {
      // Fetch animals for each type in the "other" category
      const otherAnimalTypes = ["horse", "bird", "barnyard"];
      const animalPromises = otherAnimalTypes.map(async (animalType) => {
        const response = await fetch(
          `http://localhost:3002/api/petfinder?type=${animalType}&limit=${itemsPerPage}&page=${page}`
        );
        const data = await response.json();
        return data.animals || [];
      });

      const animalResponses = await Promise.all(animalPromises);
      animalData = animalResponses.flat();
    }

    try {
      if (animalData) {
        return animalData.filter((animal) => animal && animal.photos.length > 0);
      } else if (endpoint) {
        const response = await fetch(endpoint);
        const data = await response.json();
        if (data && data.animals) {
          return data.animals.filter((animal) => animal.photos.length > 0);
        }
      }
    } catch (error) {
      console.error('Error fetching data:', error.message);
    }

    return [];
  };

  // Function to handle moving to the previous page
  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  // Function to handle moving to the next page
  const handleNextPage = () => {
    if (currentPage < total_pages) {
      setCurrentPage(currentPage + 1);
    }
  };

  // Fetch and set the pet data for the current page
  useEffect(() => {
    async function fetchData() {
      const animals = await fetchAnimalsByCategory(currentPage);
      shuffleArray(animals); // Shuffle the animals array
      setCache(animals);
    }

    fetchData();
  }, [currentPage, petType]);

  const [cache, setCache] = useState([]);

  return (
    <div className="location-specific-pets">
      <h2 className="search-results-title">Search Results for:</h2>
      <h3 className="pet-type-title">All {petType}s in the {searchText} area</h3>
      <div className="pet-card-container">
        {cache.length === 0 ? (
          <p className="no-animals-message">No animals found.</p>
        ) : (
          cache.map((pet) => (
            <PetCard
              key={pet.id}
              pet={pet}
              addToFavorites={addToFavorites}
              removeFromFavorites={removeFromFavorites}
              isFavorite={favoritePets.some((favoritePet) => favoritePet.id === pet.id)}
            />
          ))
        )}
      </div>
      <div className="pagination">
        <button onClick={handlePreviousPage} disabled={currentPage === 1}>
          Previous Page
        </button>
        <span>Page {currentPage} of {total_pages}</span>
        <button onClick={handleNextPage} disabled={currentPage === total_pages}>
          Next Page
        </button>
      </div>
    </div>
  );
};

export default LocationSpecificPets;
