import React from 'react';

function Filter({ filters, onFilterChange }) {
  const handleFilterSelect = (filterName, event) => {
    onFilterChange(filterName, event.target.value.toLowerCase()); // Ensure the value is lowercase
  };

  const renderDogFilters = () => {
    return (
      <>
        {/* Dog-specific filters */}
        <div className="filter-group">
          <label htmlFor="size">Size:</label>
          <select
            id="size"
            value={filters.size}
            onChange={(event) => handleFilterSelect('size', event)}
          >
            <option value="any">Any</option>
            <option value="small">Small</option>
            <option value="medium">Medium</option>
            <option value="large">Large</option>
          </select>
        </div>
        <div className="filter-group">
          <label htmlFor="breed">Breed:</label>
          <select
            id="breed"
            value={filters.breed}
            onChange={(event) => handleFilterSelect('breed', event)}
          >
            <option value="any">Any</option>
            <option value="breed1">Breed 1</option>
            <option value="breed2">Breed 2</option>
            {/* Add more dog breed options */}
          </select>
        </div>
        {/* Add other dog-specific filters here */}
      </>
    );
  };

  const renderCatFilters = () => {
    return (
      <>
        {/* Cat-specific filters */}
        <div className="filter-group">
          <label htmlFor="coatLength">Coat Length:</label>
          <select
            id="coatLength"
            value={filters.coatLength}
            onChange={(event) => handleFilterSelect('coatLength', event)}
          >
            <option value="any">Any</option>
            <option value="hairless">Hairless</option>
            <option value="short">Short</option>
            <option value="medium">Medium</option>
            <option value="long">Long</option>
          </select>
        </div>
        {/* Add other cat-specific filters here */}
      </>
    );
  };
  return (
    <div className="filters">
      <h2>Filters</h2>
      <div className="filter-group">
        <label htmlFor="petType">Pet Type:</label>
        <select
          id="petType"
          value={filters.petType}
          onChange={(event) => handleFilterSelect('petType', event)}
        >
          <option value="any">Any</option>
          <option value="dog">Dog</option>
          <option value="cat">Cat</option>
          <option value="others">Others</option>
        </select>
      </div>
      {(filters.petType === 'cat' || filters.petType === 'any') && renderCatFilters()}
      {(filters.petType === 'dog' || filters.petType === 'any') && renderDogFilters()}
    </div>
  );
}

export default Filter;
