import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import PetCard from './PetCard';
import '../styles/LocationSpecificPets.css';

function shuffleArray(array) {
  for (let i = array.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [array[i], array[j]] = [array[j], array[i]];
  }
}

const LocationSpecificPets = ({
  favoritePets,
  addToFavorites,
  removeFromFavorites
}) => {
  const location = useLocation();
  const state = location.state;

  // Define the currentPage state at the top level
  const [currentPage, setCurrentPage] = useState(1);
  const [petsToDisplay, setPetsToDisplay] = useState([]);
  const [loading, setLoading] = useState(true);

  // Function to fetch pets for the current page
  const fetchPetsForCurrentPage = async (page) => {
    // Replace the URL with your API endpoint
    const endpoint = `http://localhost:3002/api/petfinder?type=${state.petType}&limit=100&page=${page}`;

    try {
      const response = await fetch(endpoint);
      if (!response.ok) {
        throw new Error(`Request failed with status: ${response.status}`);
      }
      const data = await response.json();

      if (data.animals && data.animals.length > 0) {
        const startIndex = (page - 1) * 20;
        const endIndex = startIndex + 20;
        const pets = data.animals.slice(startIndex, endIndex);
        return pets;
      } else {
        return [];
      }
    } catch (error) {
      console.error('Error fetching data:', error.message);
      return [];
    }
  };

  // Function to handle moving to a different page
  const handlePageChange = async (newPage) => {
    if (newPage >= 1) {
      setCurrentPage(newPage);
      setLoading(true);
      const pets = await fetchPetsForCurrentPage(newPage);
      setPetsToDisplay(pets);
      setLoading(false);
    }
  };

  useEffect(() => {
    handlePageChange(currentPage);
  }, [state.petType, currentPage]);

  return (
    <div className="location-specific-pets">
      <h2 className="search-results-title">Search Results for:</h2>
      <h3 className="pet-type-title">All {state.petType}s in the {state.searchText} area</h3>
      <div className="pet-card-container">
        {loading ? (
          <p className="loading">Loading...</p>
        ) : petsToDisplay.length === 0 ? (
          <p className="no-animals-message">No animals found.</p>
        ) : (
          petsToDisplay.map((pet) => (
            <PetCard
              key={pet.id}
              pet={pet}
              addToFavorites={addToFavorites}
              removeFromFavorites={removeFromFavorites}
              isFavorite={favoritePets.some((favoritePet) => favoritePet.id === pet.id)}
            />
          ))
        )}
      </div>
      <div className="pagination">
        <button onClick={() => handlePageChange(currentPage - 1)} disabled={currentPage === 1}>
          Previous Page
        </button>
        <span>Page {currentPage}</span>
        <button onClick={() => handlePageChange(currentPage + 1)} disabled={petsToDisplay.length < 20}>
          Next Page
        </button>
      </div>
    </div>
  );
};

export default LocationSpecificPets;
