import React, { useState, useEffect, useRef } from 'react';
import '../styles/MainPage.css';
import PetCard from './PetCard';
import Filter from './Filter';
import { Link } from 'react-router-dom';

function MainPage({ favoritePets, addToFavorites, removeFromFavorites }) {
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(true);
  const currentPageRef = useRef(1);
  const itemsPerPage = 12;
  const minimumPets = 400;
  const [cachedData, setCachedData] = useState([]);
  const [totalPages, setTotalPages] = useState(0);

  const [selectedFilters, setSelectedFilters] = useState({
    type: 'any',
    breed: 'any',
    age: 'any',
    gender: 'any',
    size: 'any',
    coat: 'any',
  });

  // Function to fetch pets based on filters and pagination
  const fetchPets = async () => {
    try {
      const filters = Object.keys(selectedFilters)
        .filter((key) => selectedFilters[key] !== 'any')
        .map((key) => `${key}=${selectedFilters[key]}`)
        .join('&');

      const endpoint = `http://localhost:3002/api/petfinder?perPage=${itemsPerPage}&page=${currentPageRef.current}&${filters}`;
      const response = await fetch(endpoint);
      const data = await response.json();

      console.log('API Response:', data);

      if (data && data.animals) {
        setCachedData(data.animals);
        setTotalPages(Math.ceil(data.pagination.total_count / itemsPerPage));
        setSearchResults(data.animals);
        setLoading(false);
      }
    } catch (error) {
      console.error('Error fetching data:', error.message);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPets();
  }, [currentPageRef, selectedFilters]);

  const handlePageChange = (page) => {
    currentPageRef.current = page;
    // Use cached data and update searchResults when changing pages
    updateSearchResults();
  };

  // Function to handle filter changes
  const handleFilterChange = (newFilters) => {
    setSelectedFilters(newFilters);
    currentPageRef.current = 1;
    fetchPets();
  };

  // Function to update searchResults based on currentPage and cachedData
  const updateSearchResults = () => {
    const startIndex = (currentPageRef.current - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    const petsToDisplay = cachedData.slice(startIndex, endIndex);

    setSearchResults(petsToDisplay);
  };

  // Log what's in the cache
  useEffect(() => {
    console.log('Cached Data:', cachedData);
  }, [cachedData]);

  return (
    <div className="main-page">
      <div className="sidebar">
        <div className="filters">
          <Filter onFilterChange={handleFilterChange} />
        </div>
      </div>
      <div className="content">
        <div className="pet-card-list">
          {loading ? (
            <p>Loading...</p>
          ) : searchResults.length > 0 ? (
            searchResults.map((pet) => (
              <div key={pet.id} className="pet-card-container">
                <PetCard
                  pet={pet}
                  addToFavorites={addToFavorites}
                  removeFromFavorites={removeFromFavorites}
                  isFavorite={favoritePets.some((favoritePet) => favoritePet.id === pet.id)}
                />
              </div>
            ))
          ) : (
            <p>No pets match your criteria.</p>
          )}
        </div>
        {totalPages > 1 && (
          <div className="pagination-horizontal">
            {Array.from({ length: totalPages }, (_, index) => {
              const page = index + 1;
              return (
                <button
                  key={index}
                  onClick={() => {
                    handlePageChange(page);
                  }}
                  className={currentPageRef.current === page ? 'active' : ''}
                >
                  {page}
                </button>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
}

export default MainPage;
