import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/AllPetsPage.css';
import PetCard from './PetCard';

function AllPetsPage() {
  const { category } = useParams();
  const [loading, setLoading] = useState(true);
  const petsPerPage = 40;
  const [cache, setCache] = useState([]);
  const otherAnimalTypes = ["rabbit", "hamster", "pigeon"];

  const fetchAnimalsByType = async (type) => {
    const endpoint = `http://localhost:3002/api/petfinder?type=${type}&limit=${petsPerPage}`;

    try {
      const response = await fetch(endpoint);
      const data = await response.json();

      if (data && data.animals) {
        return data.animals;
      }
    } catch (error) {
      console.error('Error fetching data:', error.message);
    }

    return [];
  };

  useEffect(() => {
    async function fetchData() {
      const allPets = [];

      for (const animalType of otherAnimalTypes) {
        const otherAnimals = await fetchAnimalsByType(animalType);
        allPets.push(...otherAnimals);
      }

      setCache(allPets);
      setLoading(false);
    }

    fetchData();
  }, []);

  return (
    <div className="all-pets-page">
      <h2>{`All ${category.charAt(0).toUpperCase() + category.slice(1)}s`}</h2>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="pet-list">
          {cache.map((pet) => (
            <PetCard key={pet.id} pet={pet} />
          ))}
        </div>
      )}
    </div>
  );
}

export default AllPetsPage;
