import React, { useState, useEffect } from 'react';
import '../styles/MainPage.css';
import PetCard from './PetCard';
import Filter from './Filter';

function MainPage({ favoritePets, addToFavorites, removeFromFavorites }) {
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(true);

  // Set default filter values
  const [filters, setFilters] = useState({
    type: 'any',
    breed: 'any',
    age: 'any',
    gender: 'any',
    size: 'any',
    coat: 'any',
  });

  useEffect(() => {
    // When the component mounts, fetch all animals
    fetchAllPets();
  }, []);

  const fetchAllPets = () => {
    const endpoint = 'http://localhost:3001/api/petfinder';

    fetch(endpoint)
      .then((response) => response.json())
      .then((data) => {
        console.log('API Response:', data);
        setSearchResults(data.animals || []);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching data:', error.message);
        setLoading(false);
      });
  };

  const handleFilterChange = (newFilters) => {
  // Update the filters with the new values
  setFilters(newFilters);

  // Apply client-side filtering based on newFilters
  let filteredData = [...searchResults];

  // Filter by pet type
  if (newFilters.type && newFilters.type !== 'any') {
    filteredData = filteredData.filter((pet) => pet.type.toLowerCase() === newFilters.type.toLowerCase());
  }
  // Filter by breed
  if (newFilters.breed !== 'any') {
    filteredData = filteredData.filter((pet) => pet.breed.toLowerCase() === newFilters.breed.toLowerCase());
  }
  // Filter by age
  if (newFilters.age !== 'any') {
    filteredData = filteredData.filter((pet) => pet.age.toLowerCase() === newFilters.age);
  }
  // Filter by gender
  if (newFilters.gender !== 'any') {
    filteredData = filteredData.filter((pet) => pet.gender.toLowerCase() === newFilters.gender);
  }
  // Filter by size
  if (newFilters.size !== 'any') {
    filteredData = filteredData.filter((pet) => pet.size.toLowerCase() === newFilters.size);
  }
  // Filter by coat
  if (newFilters.coat !== 'any') {
    filteredData = filteredData.filter((pet) => pet.coat.toLowerCase() === newFilters.coat);
  }

  setSearchResults(filteredData);
};
  return (
    <div className="main-page">
      <div className="sidebar">
        <div className="filters">
          <Filter onFilterChange={handleFilterChange} />
        </div>
      </div>
      <div className="content">
        {loading ? (
          <p>Loading...</p>
        ) : (
          <div className="pet-card-list">
            {searchResults.map((pet) => (
              <PetCard
                key={pet.id}
                pet={pet}
                addToFavorites={addToFavorites}
                removeFromFavorites={removeFromFavorites}
                isFavorite={favoritePets.some((favoritePet) => favoritePet.id === pet.id)}
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default MainPage;
