import React, { useState, useEffect, useRef, useCallback } from 'react';
import '../styles/MainPage.css';
import PetCard from './PetCard';
import Filter from './Filter';

function MainPage({ favoritePets, addToFavorites, removeFromFavorites }) {
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(true);
  const currentPageRef = useRef(1);
  const itemsPerPage = 10;
  const minimumPets = 100;
  const fetchInProgress = useRef(false);

  const [selectedFilters, setSelectedFilters] = useState({
    type: 'any',
    breed: 'any',
    age: 'any',
    gender: 'any',
    size: 'any',
    coat: 'any',
  });

  // Function to fetch all pets
  const fetchAllPets = useCallback(() => {
    if (fetchInProgress.current || searchResults.length >= minimumPets) {
      return;
    }

    fetchInProgress.current = true;

    const endpoint = `http://localhost:3002/api/petfinder?page=${currentPageRef.current}&perPage=${itemsPerPage}`;

    fetch(endpoint)
      .then((response) => response.json())
      .then((data) => {
        console.log('API Data:', data);

        const updatedResults = [...searchResults, ...(data.animals || [])];

        setLoading(false);

        if (updatedResults.length < minimumPets) {
          currentPageRef.current++;
        }

        setSearchResults(updatedResults);
      })
      .catch((error) => {
        console.error('Error fetching data:', error.message);
        setLoading(false);
      })
      .finally(() => {
        fetchInProgress.current = false;
      });
  }, [searchResults, minimumPets, itemsPerPage]);

  useEffect(() => {
    // Fetch all data on component mount
    fetchAllPets();
  }, []);

  // Function to apply filters
  const applyFilters = (data, filters) => {
    return data.filter((pet) => {
      for (const filterKey in filters) {
        const filterValue = filters[filterKey]?.toLowerCase();
        if (filterValue && filterValue !== 'any') {
          const petValue = pet[filterKey]?.toLowerCase();
          if (petValue !== filterValue) {
            return false;
          }
        }
      }
      return true;
    });
  };

  // Function to handle filter changes and apply them
  const handleFilterChange = (newFilters) => {
    setSelectedFilters(newFilters);

    // Apply filters to the full list of pets
    const filteredResults = applyFilters(searchResults, newFilters);
    setSearchResults(filteredResults);
  };

  return (
    <div className="main-page">
      <div className="sidebar">
        <div className="filters">
          {/* Pass setSelectedFilters and handleFilterChange to the Filter component */}
          <Filter
            selectedFilters={selectedFilters}
            onFilterChange={handleFilterChange}
            onSearchClick={handleSearchClick}
          />
        </div>
      </div>
      <div className="content">
        {!loading ? (
          <div className="pet-card-list">
            {searchResults.length > 0 ? (
              searchResults.map((pet) => (
                <PetCard
                  key={pet.id}
                  pet={pet}
                  addToFavorites={addToFavorites}
                  removeFromFavorites={removeFromFavorites}
                  isFavorite={favoritePets.some((favoritePet) => favoritePet.id === pet.id)}
                />
              ))
            ) : (
              <p>No pets match your criteria.</p>
            )}
          </div>
        ) : (
          <p>Loading...</p>
        )}
      </div>
    </div>
  );
}

export default MainPage;
