import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/AllPetsPage.css';
import PetCard from './PetCard';


function AllPetsPage() {
  const { category } = useParams();
  const [pets, setPets] = useState([]);
  const [loading, setLoading] = useState(true);


// Function to fetch animals of a specific type
const fetchAnimalsByType = async (type) => {
    try {
      const endpoint = `https://api.petfinder.com/v2/animals?type=${type}`;
      // Include the 'Authorization' header with the 'Bearer' token
      const response = await fetch(endpoint);
      const data = await response.json();

      console.log('API Response:', data);

      if (data && data.animals) {
        setPets(data.animals);
        setLoading(false);
      }
    } catch (error) {
      console.error('Error fetching data:', error.message);
      setLoading(false);
    }
  };



  useEffect(() => {
    fetchAnimalsByType(category);
  }, [category]);

  return (
    <div className="all-pets-page">
      <h2>{`All ${category.charAt(0).toUpperCase() + category.slice(1)}`}</h2>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="pet-list">
          {pets.map((pet) => (
            <PetCard
              key={pet.id}
              pet={pet}
            />
          ))}
        </div>
      )}
    </div>
  );
}

export default AllPetsPage;
