import React, { useState } from 'react';

const PetCard = ({ pet }) => {
  const [favorited, setFavorited] = useState(pet.favorited);

  const handleToggleFavorite = () => {
    setFavorited(!favorited);
  };

  // Truncate the description to one line with "..." at the end
  const truncatedDescription = pet.description.length > 100 ? `${pet.description.substring(0, 100)}...` : pet.description;

  return (
    <div className="pet-card">
      <div className="pet-card-header">
        <img src={pet.photo} alt={pet.name} className="pet-card-image" />
        <p className="pet-card-distance">{pet.distance} miles away</p>
      </div>
      <div className="pet-card-info">
        <div>
          <h3>{pet.name}</h3>
          <p>{pet.size} | {pet.age} | {pet.gender} </p>
        </div>
        <div>
          <p>{pet.breed} </p>
        </div>
      </div>
      <p className="pet-card-description">{truncatedDescription}</p>
      <div className="pet-card-footer">
        <button
          className={`favorite-heart ${favorited ? 'favorited' : ''}`}
          onClick={handleToggleFavorite}
        >
          &#10084;
        </button>
        <button className="more-info-button">More Info</button>
      </div>
    </div>
  );
};

export default PetCard;
