import React, { useState, useCallback, useRef, useEffect } from 'react';
import '../styles/MainPage.css';
import PetCard from './PetCard';
import Filter from './Filter';

function MainPage({ favoritePets, addToFavorites, removeFromFavorites }) {
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(true);
  const itemsPerPage = 10;
  const minimumPets = 10; // Change this to the minimum number of pets you want to display
  const fetchInProgress = useRef(false);
  const currentPageRef = useRef(1);

  const fetchAllPets = useCallback(() => {
    if (fetchInProgress.current) {
      return;
    }

    fetchInProgress.current = true;

    const endpoint = `http://localhost:3002/api/petfinder?page=${currentPageRef.current}&perPage=${itemsPerPage}`;

    fetch(endpoint)
      .then((response) => response.json())
      .then((data) => {
        console.log('API Data:', data);

        const existingPetIds = searchResults.map((pet) => pet.id);
        const uniquePets = data.animals.filter((pet) => !existingPetIds.includes(pet.id));
        const updatedResults = [...searchResults, ...uniquePets];

        if (updatedResults.length < minimumPets) {
          currentPageRef.current++;
        }

        setSearchResults(updatedResults);
      })
      .catch((error) => {
        console.error('Error fetching data:', error.message);
      })
      .finally(() => {
        fetchInProgress.current = false;
        setLoading(false);
      });
  }, [searchResults, minimumPets, itemsPerPage]);

  useEffect(() => {
    fetchAllPets();
  }, [fetchAllPets]);

  return (
    <div className="main-page">
      <div className="sidebar">
        <div className="filters">
          <Filter onSearchClick={fetchAllPets} />
        </div>
      </div>
      <div className="content">
        {!loading ? (
          <div className="pet-card-list">
            {searchResults.map((pet) => (
              <PetCard
                key={pet.id}
                pet={pet}
                addToFavorites={addToFavorites}
                removeFromFavorites={removeFromFavorites}
                isFavorite={favoritePets.some((favoritePet) => favoritePet.id === pet.id)}
              />
            ))}
          </div>
        ) : (
          <p>Loading...</p>
        )}
      </div>
    </div>
  );
}

export default MainPage;
