import React, { useState, useEffect } from 'react';
import PlacesAutocomplete, { geocodeByAddress, getLatLng } from 'react-places-autocomplete';
import '../styles/SearchBar.css';

const SearchBar = ({ onSearch }) => {
  const [searchText, setSearchText] = useState('');
  const [petType, setPetType] = useState('');
  const [address, setAddress] = useState('');
  const [suggestions, setSuggestions] = useState([]);

  const handleSearch = async () => {
    if (searchText && petType) {
      // Both location and type are provided, so we can query the API
      const apiEndpoint = `http://localhost:3002/api/petfinder?perPage=100&location=${searchText}&type=${petType}`;

      try {
        const response = await fetch(apiEndpoint);
        if (response.ok) {
          const data = await response.json();
          console.log(data);
          onSearch(data);
        } else {
          console.error('API request failed:', response.statusText);
        }
      } catch (error) {
        console.error('API request error:', error);
      }
    } else {
      // Location or type is missing, so you can display an error message or prevent the search
      alert('Please enter both location and pet type to search.');
    }
  };

  const handleInput = (input) => {
    setSearchText(input);
  };

  const handleSelect = async (selectedAddress) => {
    setAddress(selectedAddress);
    setSearchText(selectedAddress); // Update the input with the selected address
    setSuggestions([]);

    const results = await geocodeByAddress(selectedAddress);
    const latLng = await getLatLng(results[0]);

    // Now you have the latitude and longitude, which you can use for searching
    onSearch({ address: selectedAddress, latLng });
  };

  const handleFetchSuggestions = (input) => {
    setAddress(input);
    fetchSuggestions(input);
  };

  const fetchSuggestions = (input) => {
    // Use Google Places API to fetch suggestions based on the input
    const google = window.google; // Ensure that "google" is defined
    if (google && google.maps.places) {
      const autocompleteService = new google.maps.places.AutocompleteService();
      autocompleteService.getPlacePredictions(
        { input, types: ['(cities)'] },
        (predictions, status) => {
          if (status === google.maps.places.PlacesServiceStatus.OK) {
            setSuggestions(predictions);
          }
        }
      );
    }
  };

  useEffect(() => {
    // Load the Google Maps service after the component mounts
    if (window.google && window.google.maps) {
      const google = window.google;
      const sydney = new google.maps.LatLng(-33.867, 151.195);
      const map = new google.maps.Map(document.getElementById('map'), {
        center: sydney,
        zoom: 15
      });
    }
  }, []);

  return (
    <div className="search-bar">
      <PlacesAutocomplete
        value={address}
        onChange={handleFetchSuggestions}
        onSelect={handleSelect}
      >
        {({ getInputProps }) => (
          <div className="search-container">
            <div className="search-input-container">
              <input
                {...getInputProps()}
                type="text"
                placeholder="Enter zipcode, city, or state"
                className="search-input"
              />
              <div className="autocomplete-dropdown-container">
                {suggestions.map((suggestion) => (
                  <div
                    key={suggestion.id}
                    className="suggestion-item"
                    onClick={() => handleSelect(suggestion.description)}
                  >
                    {suggestion.description}
                  </div>
                ))}
              </div>
            </div>
            <select
              value={petType}
              onChange={(e) => setPetType(e.target.value)}
              className="search-select"
            >
              <option value="">Select pet type</option>
              <option value="dog">Dog</option>
              <option value="cat">Cat</option>
              <option value="horse">Horse</option>
              <option value="bird">Bird</option>
              <option value="furry">Furry</option>
              <option value="barnyard">Barnyard</option>
            </select>
            <button onClick={handleSearch} className="search-button">
              Search
            </button>
          </div>
        )}
      </PlacesAutocomplete>
    </div>
  );
};

export default SearchBar;
